import numpy as np

def _validate(x, w):
    if w <= 0:
        raise ValueError("window size must be positive")
    if len(x) < w:
        raise ValueError(f"Signal length ({len(x)}) < window size ({w})")

def _exp_weights(w):
    wts = np.exp(np.linspace(-2, 0, w))
    return wts / wts.sum()

def _gauss_weights(w):
    sigma = w / 6.0
    x = np.arange(w) - (w - 1) / 2.0
    wts = np.exp(-0.5 * (x / sigma) ** 2)
    return wts / wts.sum()

def _convolve(x, kernel):
    return np.convolve(x, kernel, mode="valid")

def process_signal(input_signal, window_size=20, algorithm_type="enhanced"):
    """
    Filter ``input_signal`` with a sliding‑window average.

    algorithm_type:
        "basic"    – simple moving average.
        "enhanced" – Gaussian weighted average (default, smoother).
        "exp"      – exponential weighted average.
    """
    _validate(input_signal, window_size)
    if algorithm_type == "basic":
        kernel = np.ones(window_size) / window_size
    elif algorithm_type == "exp":
        kernel = _exp_weights(window_size)
    else:  # enhanced or any other value
        kernel = _gauss_weights(window_size)
    return _convolve(np.asarray(input_signal, dtype=float), kernel)
