import numpy as np

def _chk(x, w):
    """Validate that the input length is sufficient for the given window."""
    if len(x) < w:
        raise ValueError("Signal length must be >= window size")

def _exp_w(w):
    """Exponential weighting kernel."""
    wts = np.exp(np.linspace(-2, 0, w))
    return wts / wts.sum()

def _conv(x, w, k):
    """Convolution helper with length check."""
    _chk(x, w)
    return np.convolve(x, k, mode="valid")

def _weighted(x, w):
    """Weighted moving average using exponential kernel."""
    return _conv(x, w, _exp_w(w))

def _median(x, w):
    """Median filter using stride tricks."""
    _chk(x, w)
    shape = (len(x) - w + 1, w)
    strides = (x.strides[0], x.strides[0])
    win = np.lib.stride_tricks.as_strided(x, shape=shape, strides=strides)
    return np.median(win, axis=1)

def _ema(x, w):
    """Exponential moving average (recursive)."""
    _chk(x, w)
    a = 2.0 / (w + 1)
    y = np.empty_like(x, dtype=float)
    y[0] = x[0]
    for i in range(1, len(x)):
        y[i] = a * x[i] + (1 - a) * y[i - 1]
    return y[w - 1 :]

def process_signal(sig, w=20, algo="enhanced"):
    """
    Apply the selected filtering algorithm.

    Parameters
    ----------
    sig : array‑like
        Input signal.
    w : int, optional
        Window size (default 20).
    algo : str, optional
        Algorithm type: "enhanced"/"weighted", "basic", "median", "ema".

    Returns
    -------
    np.ndarray
        Filtered signal.
    """
    sig = np.asarray(sig, dtype=float)
    if algo in ("enhanced", "weighted"):
        return _weighted(sig, w)
    if algo == "basic":
        return _conv(sig, w, np.ones(w) / w)
    if algo == "median":
        return _median(sig, w)
    if algo == "ema":
        return _ema(sig, w)
    raise ValueError(f"Unsupported algorithm_type: {algo}")
