import numpy as np
def _kernel(w,k):
    if k=="gaussian":
        s=w/6.;t=np.arange(w)-(w-1)/2.;c=np.exp(-.5*(t/s)**2)
    elif k=="exponential":
        c=np.exp(np.linspace(-2,0,w))
    else:
        c=np.ones(w)
    return c/c.sum()
def _detrend(x,w,m):
    if m=="median":
        v=np.lib.stride_tricks.sliding_window_view(x,w)
        m=np.median(v,axis=1)
        p=(len(x)-len(m))//2
        m=np.pad(m,(p,len(x)-len(m)-p),mode="edge")
    else:
        m=np.convolve(x,np.ones(w)/w,mode="same")
    return x-m
def process_signal(sig,w=20,method="enhanced"):
    x=np.asarray(sig,float)
    if method in ("enhanced","robust"):
        x=_detrend(x,w,"median" if method=="robust" else "mean")
        k="exponential"
    elif method=="simple":k="uniform"
    elif method=="gaussian":k="gaussian"
    elif method=="median":
        v=np.lib.stride_tricks.sliding_window_view(x,w)
        return np.median(v,axis=1)
    elif method=="ema":
        a=2.0/(w+1);y=np.empty_like(x);y[0]=x[0]
        for i in range(1,len(x)):y[i]=a*x[i]+(1-a)*y[i-1]
        return y[w-1:]
    else:k=method
    y=np.convolve(x,_kernel(w,k),mode="valid")
    a=2.0/(w+1);out=np.empty_like(y);out[0]=y[0]
    for i in range(1,len(y)):out[i]=a*y[i]+(1-a)*out[i-1]
    return out
