import numpy as np

def _chk(x, w):
    if len(x) < w:
        raise ValueError("Signal shorter than window")

def _kernel(w, kind):
    if kind == "basic":
        k = np.ones(w)
    else:  # enhanced exponential
        k = np.exp(np.linspace(-2.0, 0, w))
    return k / k.sum()

def _filter(sig, w, kind):
    _chk(sig, w)
    return np.convolve(sig, _kernel(w, kind), mode="valid")

def _median(sig, w):
    _chk(sig, w)
    out = np.empty(len(sig) - w + 1, dtype=float)
    for i in range(len(out)):
        out[i] = np.median(sig[i:i + w])
    return out

def process_signal(sig, window=20, algorithm_type="enhanced"):
    a = np.asarray(sig, dtype=float)
    if algorithm_type == "basic":
        return _filter(a, window, "basic")
    if algorithm_type == "median":
        return _median(a, window)
    return _filter(a, window, "enhanced")
