def moving_average(x, w=20):
    """Simple moving average."""
    if len(x) < w:
        raise ValueError("Signal shorter than window size")
    return np.convolve(x, np.ones(w) / w, mode="valid")


def weighted_moving_average(x, w=20):
    """Weighted (exponential) moving average for trend preservation."""
    if len(x) < w:
        raise ValueError("Signal shorter than window size")
    weights = np.exp(np.linspace(-2, 0, w))
    weights /= weights.sum()
    return np.convolve(x, weights, mode="valid")


def process_signal(signal, window=20, algorithm_type="enhanced"):
    """
    Apply the selected filtering algorithm.

    Parameters
    ----------
    signal : array‑like
        Input time‑series.
    window : int, optional
        Window length (default 20).
    algorithm_type : {"enhanced", "basic"}, optional
        Choose weighted (enhanced) or simple (basic) moving average.

    Returns
    -------
    ndarray
        Filtered signal.
    """
    sig = np.asarray(signal, dtype=float)
    if algorithm_type == "enhanced":
        return weighted_moving_average(sig, window)
    return moving_average(sig, window)
