import numpy as np
from functools import lru_cache

@lru_cache(maxsize=None)
def _kernel(w, kind):
    if kind == "gaussian":
        s = w / 6.0
        t = np.arange(w) - (w - 1) / 2
        k = np.exp(-0.5 * (t / s) ** 2)
    elif kind in ("exp", "exponential"):
        k = np.exp(np.linspace(-2, 0, w))
    else:
        k = np.ones(w)
    return k / k.sum()

def _mad(x):
    m = np.median(x)
    return np.median(np.abs(x - m))

def _detrend(x, w):
    if w and len(x) >= w:
        return x - np.convolve(x, np.ones(w) / w, "same")
    return x

def adaptive_filter(x, w):
    return np.convolve(x, np.ones(w) / w, "valid")

def enhanced_filter(x, w):
    x = _detrend(x, w)
    kind = "gaussian" if _mad(x) < 0.05 else "exp"
    return np.convolve(x, _kernel(w, kind), "valid")

def process_signal(sig, window_size=20, algorithm_type="enhanced"):
    x = np.asarray(sig, float)
    if algorithm_type == "enhanced":
        return enhanced_filter(x, window_size)
    return adaptive_filter(x, window_size)
