import numpy as np
from functools import lru_cache

@lru_cache(None)
def _kernel(w, kind):
    t = np.arange(w) - (w - 1) / 2
    if kind == "gaussian":
        s = w / 6.0
        k = np.exp(-0.5 * (t / s) ** 2)
    elif kind == "hann":
        k = np.hanning(w)
    elif kind in ("exponential", "exp"):
        k = np.exp(np.linspace(-2, 0, w))
    else:
        k = np.ones(w)
    return k / k.sum()

def _mad(x):
    m = np.median(x)
    return np.median(np.abs(x - m))

def _auto_kind(x):
    mad = _mad(x)
    var = np.var(x)
    return "gaussian" if mad < 0.02 and var < 0.02 else "hann" if mad < 0.08 else "exponential"

def _median_filter(x, w):
    p = w // 2
    a = np.pad(x, (p, p), mode="edge")
    shape = (len(x), w)
    strides = (a.strides[0], a.strides[0])
    windows = np.lib.stride_tricks.as_strided(a, shape, strides)
    return np.median(windows, axis=1)

def process_signal(sig, window=20, alg="enhanced", full=False):
    if window <= 0:
        raise ValueError("window must be positive")
    x = np.asarray(sig, float)
    if np.isnan(x).any():
        idx = np.arange(len(x))
        mask = np.isfinite(x)
        x = np.interp(idx, idx[mask], x[mask])
    if alg == "enhanced":
        x = x - np.convolve(x, np.ones(window) / window, mode="same")
        kind = _auto_kind(x)
        mode = "same" if full else "valid"
        y = np.convolve(x, _kernel(window, kind), mode=mode)
    elif alg == "simple":
        mode = "same" if full else "valid"
        y = np.convolve(x, _kernel(window, "uniform"), mode=mode)
    elif alg == "median":
        y = _median_filter(x, window)
        if not full:
            return y
    else:
        mode = "same" if full else "valid"
        y = np.convolve(x, _kernel(window, alg), mode=mode)
    return y
