def _to_array(x):
    """Convert input to a 1‑D NumPy float array."""
    arr = np.asarray(x, dtype=float).ravel()
    return arr


def _validate_window(arr, w):
    """Validate window size `w` for a given array.

    Returns ``True`` if the window is usable, otherwise ``False``.
    Raises ``ValueError`` for non‑positive or non‑integer ``w``.
    """
    if not isinstance(w, (int, np.integer)):
        raise TypeError("window size must be an integer")
    if w <= 0:
        raise ValueError("window size must be positive")
    return arr.size >= w


# cache for exponential weights to avoid recomputation
_weights_cache = {}


def _exp_weights(w):
    """Return normalized exponential weights for a given window size."""
    if w not in _weights_cache:
        wgt = np.exp(np.linspace(-2, 0, w))
        wgt /= wgt.sum()
        _weights_cache[w] = wgt
    return _weights_cache[w]


def adaptive(x, w=20):
    """Simple moving‑average filter (uniform kernel)."""
    a = _to_array(x)
    if not _validate_window(a, w):
        return np.empty(0, dtype=float)
    kernel = np.ones(w, dtype=float) / w
    return np.convolve(a, kernel, mode="valid")


def weighted(x, w=20):
    """Exponential weighted moving‑average filter."""
    a = _to_array(x)
    if not _validate_window(a, w):
        return np.empty(0, dtype=float)
    weights = _exp_weights(w)
    return np.convolve(a, weights, mode="valid")


def ema(x, w=20):
    """Classic exponential moving average (EMA)."""
    a = _to_array(x)
    if not _validate_window(a, w):
        return np.empty(0, dtype=float)
    alpha = 2.0 / (w + 1)
    e = np.empty_like(a)
    e[0] = a[0]
    for i in range(1, a.size):
        e[i] = alpha * a[i] + (1 - alpha) * e[i - 1]
    return e[w - 1 :]


def enhanced(x, w=20):
    """Detrend with simple moving average, then apply exponential weighting."""
    a = _to_array(x)
    if not _validate_window(a, w):
        return np.empty(0, dtype=float)
    trend = adaptive(a, w)
    if trend.size == 0:
        return np.empty(0, dtype=float)
    # pad the trend to the original length (repeat first trend value)
    trend_full = np.concatenate((np.full(w - 1, trend[0]), trend))
    detrended = a - trend_full
    return weighted(detrended, w)


def median_filter(x, w=20):
    """Sliding‑window median filter (odd window enforced)."""
    a = _to_array(x)
    if not _validate_window(a, w):
        return np.empty(0, dtype=float)
    if w % 2 == 0:
        w += 1  # make odd for symmetry
    shape = (a.size - w + 1, w)
    strides = (a.strides[0], a.strides[0])
    windows = np.lib.stride_tricks.as_strided(a, shape=shape, strides=strides)
    return np.median(windows, axis=1)


def fft_denoise(x, keep_ratio=0.1):
    """Frequency‑domain denoising using a low‑pass mask.

    ``keep_ratio`` must be between 0 (exclusive) and 1 (inclusive).
    """
    a = _to_array(x)
    if a.size == 0:
        return np.empty(0, dtype=float)
    if not (0 < keep_ratio <= 1):
        raise ValueError("keep_ratio must be in (0, 1]")
    coeffs = np.fft.rfft(a)
    mags = np.abs(coeffs)
    k = max(1, int(len(mags) * keep_ratio))
    # keep the k largest magnitudes
    idx = np.argpartition(mags, -k)[-k:]
    mask = np.zeros_like(mags, dtype=bool)
    mask[idx] = True
    filtered = np.where(mask, coeffs, 0)
    return np.fft.irfft(filtered, n=a.size)


def _residual_variance(orig, filt):
    """Variance of the residual (orig - filt) over the overlapping region."""
    n = min(len(orig), len(filt))
    if n == 0:
        return float("inf")
    residual = orig[:n] - filt[:n]
    return float(np.var(residual))


def process_signal(sig, w=20, alg="enh"):
    """Dispatch to the requested filtering algorithm.

    Supported ``alg`` values (case‑insensitive):
        - "basic", "adaptive"
        - "weighted"
        - "ema", "exponential"
        - "enh", "enhanced"
        - "median", "med"
        - "fft", "spectral"

    Any other value triggers automatic selection based on the lowest
    residual variance.
    """
    a = _to_array(sig)
    if not _validate_window(a, w):
        return np.empty(0, dtype=float)

    alg = (alg or "auto").lower()
    if alg in ("basic", "adaptive"):
        return adaptive(a, w)
    if alg == "weighted":
        return weighted(a, w)
    if alg in ("ema", "exponential"):
        return ema(a, w)
    if alg in ("enh", "enhanced"):
        return enhanced(a, w)
    if alg in ("median", "med"):
        return median_filter(a, w)
    if alg in ("fft", "spectral"):
        return fft_denoise(a, keep_ratio=0.1)

    # Automatic selection: evaluate all candidates and pick the one
    # with the smallest residual variance.
    candidates = {
        "basic": adaptive(a, w),
        "weighted": weighted(a, w),
        "ema": ema(a, w),
        "enhanced": enhanced(a, w),
        "median": median_filter(a, w),
        "fft": fft_denoise(a, keep_ratio=0.1),
    }
    # Discard empty results
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
        return np.empty(0, dtype=float)
    best_key = min(candidates, key=lambda k: _residual_variance(a, candidates[k]))
    return candidates[best_key]
