import numpy as np
from functools import lru_cache

@lru_cache(maxsize=None)
def _kernel(kind: str, w: int) -> np.ndarray:
    """Return the convolution kernel for the requested kind."""
    if w == 1:
        return np.ones(1)
    if kind == "adaptive":
        return np.ones(w) / w
    # enhanced: product of exponential and Gaussian profiles
    idx = np.arange(w)
    exp = np.exp(-0.6 * (w - 1 - idx))
    mid = (w - 1) / 2.0
    sigma = max(w / 6.0, 1e-8)
    gauss = np.exp(-0.5 * ((idx - mid) / sigma) ** 2)
    k = exp * gauss
    k /= k.sum()
    return k

def _validate(x, w):
    a = np.asarray(x, float)
    if w <= 0:
        raise ValueError("window_size must be positive")
    if a.ndim != 1:
        raise ValueError("input must be 1‑D")
    if a.size < w:
        raise ValueError(f"input length ({a.size}) < window_size ({w})")
    return a

def process_signal(sig, w=20, alg="enhanced"):
    """
    Filter `sig` using the specified algorithm.

    Supported algorithms (case‑insensitive):
        - "adaptive": simple moving average
        - "enhanced": exponential‑Gaussian weighted average
    """
    a = _validate(sig, w)
    kind = alg.lower()
    if kind.startswith("a"):
        kind = "adaptive"
    elif kind.startswith("e"):
        kind = "enhanced"
    else:
        raise ValueError(f"Unsupported algorithm_type: {alg}")
    kernel = _kernel(kind, w)
    return np.convolve(a, kernel[::-1], mode="valid") if w > 1 else a.copy()
