import numpy as np
from functools import lru_cache

def _prepare(x, w):
    w = int(w)
    if w <= 0:
        raise ValueError("window_size must be positive")
    a = np.asarray(x, float).ravel()
    if a.size < w:
        return np.empty(0, float), w
    return a, w

@lru_cache(maxsize=32)
def _weights(w):
    w = int(w)
    wt = np.exp(np.linspace(-2, 0, w))
    return wt / wt.sum()

def adaptive_filter(x, window_size=20):
    a, w = _prepare(x, window_size)
    if a.size == 0:
        return a
    if w == 1:
        return a.copy()
    c = np.cumsum(np.insert(a, 0, 0.0))
    return (c[w:] - c[:-w]) / w

def enhanced_filter(x, window_size=20):
    a, w = _prepare(x, window_size)
    if a.size == 0:
        return a
    if w == 1:
        return a.copy()
    return np.convolve(a, _weights(w), mode="valid")

def process_signal(sig, window_size=20, algorithm_type="enhanced"):
    typ = algorithm_type.lower()
    if typ == "basic":
        return adaptive_filter(sig, window_size)
    if typ == "enhanced":
        return enhanced_filter(sig, window_size)
    return enhanced_filter(sig, window_size)
