        wlen = ws
        if wlen % 2 == 0:
            wlen += 1
        wlen = max(wlen, polyorder + 2)
        if wlen % 2 == 0:
            wlen += 1
        return signal.savgol_filter(arr, window_length=wlen, polyorder=polyorder, mode='interp')
    except Exception:
