def _prepare_signal(x: int, w: int) -> "np.ndarray":
    """Validate input and convert to 1‑D float array."""
    if w <= 0:
        raise ValueError(f"window size must be positive, got {w}")
    arr = np.asarray(x, dtype=float).ravel()
    if arr.size < w:
        raise ValueError(f"input length ({arr.size}) < window size ({w})")
    return arr


_weights_cache: dict[int, "np.ndarray"] = {}


def _exp_weights(w: int) -> "np.ndarray":
    """Cached right‑biased exponential weights that sum to 1."""
    if w not in _weights_cache:
        v = np.exp(np.linspace(-2, 0, w))
        _weights_cache[w] = v / v.sum()
    return _weights_cache[w]


def adaptive_filter(x, w: int = 20):
    """Simple moving‑average filter."""
    a = _prepare_signal(x, w)
    return np.convolve(a, np.ones(w) / w, mode="valid")


def enhanced_filter(x, w: int = 20):
    """Exponential‑weighted moving average (most recent samples heavier)."""
    a = _prepare_signal(x, w)
    return np.convolve(a, _exp_weights(w)[::-1], mode="valid")


def median_filter(x, w: int = 21):
    """Median filter; forces odd window size."""
    if w % 2 == 0:
        w += 1
    a = _prepare_signal(x, w)
    shape = (a.size - w + 1, w)
    strides = (a.strides[0], a.strides[0])
    windows = np.lib.stride_tricks.as_strided(a, shape=shape, strides=strides)
    return np.median(windows, axis=1)


def process_signal(sig, w: int = 20, algo: str = "enhanced"):
    """Dispatch to the requested filtering algorithm (case‑insensitive)."""
    a = algo.lower()
    if a == "adaptive":
        return adaptive_filter(sig, w)
    if a == "median":
        return median_filter(sig, w)
    return enhanced_filter(sig, w)
