import numpy as np

def _to_array(x, w):
    a = np.asarray(x, float).ravel()
    if w <= 0:
        raise ValueError("window size must be positive")
    if a.size < w:
        return np.empty(0, float)
    return a

def adaptive_filter(x, w=20):
    a = _to_array(x, w)
    if a.size == 0:
        return a
    c = np.cumsum(np.insert(a, 0, 0.0))
    return (c[w:] - c[:-w]) / w

def weighted_filter(x, w=20):
    a = _to_array(x, w)
    if a.size == 0:
        return a
    k = np.exp(np.linspace(-2, 0, w))
    k /= k.sum()
    return np.convolve(a, k, mode='valid')

def exponential_smoothing(x, w=20):
    a = _to_array(x, w)
    if a.size == 0:
        return a
    alpha = 2.0 / (w + 1.0)
    y = np.empty_like(a)
    y[0] = a[0]
    for i in range(1, a.size):
        y[i] = alpha * a[i] + (1.0 - alpha) * y[i - 1]
    return y[w - 1 :]

def process_signal(sig, w=20, typ="enhanced"):
    t = typ.lower()
    if t == "basic":
        return adaptive_filter(sig, w)
    if t == "exp":
        return exponential_smoothing(sig, w)
    return weighted_filter(sig, w)
