import numpy as np
from functools import lru_cache

try:
    from scipy.signal import savgol_filter
    _HAS_SAVGOL = True
except Exception:
    _HAS_SAVGOL = False


def _as_array(x, w):
    """Validate input and convert to 1‑D float array."""
    a = np.asarray(x, dtype=float).ravel()
    w = int(w)
    if w <= 0:
        raise ValueError("window_size must be positive")
    if a.size < w:
        raise ValueError(f"Input length ({a.size}) < window_size ({w})")
    return a, w


@lru_cache(maxsize=32)
def _exp_weights(ws: int) -> np.ndarray:
    """Pre‑computed exponential weights for EWMA."""
    w = np.exp(np.linspace(-2, 0, ws))
    return w / w.sum()


def adaptive_filter(x, w=20):
    """Fast simple moving‑average using convolution."""
    a, w = _as_array(x, w)
    return np.convolve(a, np.ones(w) / w, mode="valid")


def _apply_savgol(signal: np.ndarray, win: int, polyorder: int = 3) -> np.ndarray:
    """Apply Savitzky‑Golay filter with safe fallback."""
    if not _HAS_SAVGOL:
        return signal
    # Ensure odd window and at least polyorder+2
    if win % 2 == 0:
        win += 1
    win = max(win, polyorder + 2)
    if win % 2 == 0:
        win += 1
    return savgol_filter(signal, window_length=win, polyorder=polyorder, mode="interp")


def enhanced_filter(x, w=20):
    """
    Enhanced filter: EWMA → adaptive Savitzky‑Golay smoothing → short MA.
    Returns a 1‑D array whose length is ``len(x) - w + 1 - 2`` (valid after final MA).
    """
    a, w = _as_array(x, w)

    # 1️⃣ Exponential weighted moving average (valid convolution)
    ewma = np.convolve(a, _exp_weights(w), mode="valid")

    # 2️⃣ Adaptive Savitzky‑Golay: window size scales with signal variability
    std = np.std(ewma)
    mean_abs = np.mean(np.abs(ewma)) + 1e-12
    scale = std / mean_abs
    adaptive_win = max(5, min(w, int(w * (0.5 + scale))))
    # Ensure odd
    if adaptive_win % 2 == 0:
        adaptive_win += 1

    sg = _apply_savgol(ewma, adaptive_win, polyorder=2)

    # 3️⃣ Final short moving average (window=3) to clean residual high‑frequency noise
    final = np.convolve(sg, np.ones(3) / 3, mode="valid")
    return final


def exponential_smoothing(x, w=20):
    """Recursive exponential smoothing; output aligns with other filters."""
    a, w = _as_array(x, w)
    alpha = 2.0 / (w + 1.0)
    y = np.empty_like(a)
    y[0] = a[0]
    for i in range(1, a.size):
        y[i] = alpha * a[i] + (1.0 - alpha) * y[i - 1]
    return y[w - 1 :]


def process_signal(sig, w=20, typ="enhanced"):
    """Dispatch to the selected filtering algorithm (case‑insensitive)."""
    typ = typ.lower()
    if typ == "basic":
        return adaptive_filter(sig, w)
    if typ == "exp":
        return exponential_smoothing(sig, w)
    # default / "enhanced"
    return enhanced_filter(sig, w)
