def _to_array(x):
    return np.asarray(x, float)


def _simple_ma(x, w):
    if w <= 0 or x.size < w:
        return np.empty(0, float)
    return np.convolve(x, np.ones(w) / w, "valid")


def _weighted_ma(x, w):
    if w <= 0 or x.size < w:
        return np.empty(0, float)
    wt = np.exp(np.linspace(-2, 0, w))
    wt /= wt.sum()
    return np.convolve(x, wt, "valid")


def _ema(x, w):
    if w <= 0 or x.size < w:
        return np.empty(0, float)
    alpha = 2.0 / (w + 1)
    out = np.empty_like(x)
    out[0] = x[0]
    for i in range(1, len(x)):
        out[i] = alpha * x[i] + (1 - alpha) * out[i - 1]
    return out[w - 1 :]


def _enhanced(x, w):
    # Detrend with simple moving average then apply weighted smoothing.
    trend = _simple_ma(x, w)
    if trend.size == 0:
        return np.empty(0, float)
    trend_full = np.concatenate((np.full(w - 1, trend[0]), trend))
    detrended = x - trend_full
    weighted = _weighted_ma(detrended, w)

    # Optional second‑stage smoothing (half‑window) to suppress residual spikes.
    if weighted.size == 0:
        return weighted
    half = max(1, w // 2)
    if weighted.size >= half:
        weighted = np.convolve(weighted, np.ones(half) / half, "valid")
    return weighted


def _residual_variance(orig, filt):
    m = min(len(orig), len(filt))
    if m == 0:
        return np.inf
    return float(np.var(orig[:m] - filt[:m]))


def _select_best(x, w):
    candidates = {
        "basic": _simple_ma(x, w),
        "weighted": _weighted_ma(x, w),
        "ema": _ema(x, w),
        "enhanced": _enhanced(x, w),
    }
    # Choose the candidate with the smallest residual variance.
    best_key = min(candidates, key=lambda k: _residual_variance(x, candidates[k]))
    return candidates[best_key]


def process_signal(input_signal, window_size=20, algorithm_type="enhanced"):
    """
    Apply a filtering algorithm.
    Supported ``algorithm_type`` values:
        - "basic"
        - "weighted"
        - "ema"
        - "enhanced"
        - any other value → automatic selection of the best filter.
    """
    x = _to_array(input_signal)
    dispatch = {
        "basic": _simple_ma,
        "weighted": _weighted_ma,
        "ema": _ema,
        "enhanced": _enhanced,
    }
    func = dispatch.get(algorithm_type.lower())
    if func:
        return func(x, window_size)
    # fallback to automatic selection
    return _select_best(x, window_size)
