import numpy as np

def _arr(x):
    return np.asarray(x, float)


def _ok(a, w):
    return w > 0 and a.size >= w


def adaptive(x, w=20):
    a = _arr(x)
    if not _ok(a, w):
        return np.empty(0, float)
    return np.convolve(a, np.ones(w) / w, mode="valid")


def weighted(x, w=20):
    a = _arr(x)
    if not _ok(a, w):
        return np.empty(0, float)
    wt = np.exp(np.linspace(-2, 0, w))
    wt /= wt.sum()
    return np.convolve(a, wt, mode="valid")


def ema(x, w=20):
    a = _arr(x)
    if not _ok(a, w):
        return np.empty(0, float)
    alpha = 2.0 / (w + 1)
    out = np.empty_like(a)
    out[0] = a[0]
    for i in range(1, len(a)):
        out[i] = alpha * a[i] + (1 - alpha) * out[i - 1]
    return out[w - 1 :]


def enhanced(x, w=20):
    a = _arr(x)
    if not _ok(a, w):
        return np.empty(0, float)
    trend = adaptive(a, w)
    if trend.size == 0:
        return np.empty(0, float)
    trend_full = np.concatenate((np.full(w - 1, trend[0]), trend))
    return weighted(a - trend_full, w)


def process_signal(sig, w=20, alg="enhanced"):
    alg = alg.lower()
    if alg in ("basic", "adaptive"):
        return adaptive(sig, w)
    if alg == "ema":
        return ema(sig, w)
    return enhanced(sig, w)
