import numpy as np
from functools import lru_cache

def _to_array(x):
    """Convert input to a 1‑D float ndarray."""
    return np.asarray(x, dtype=float).ravel()

def _valid(arr, w):
    """Check that window size is positive and fits the array."""
    return w > 0 and arr.size >= w

@lru_cache(maxsize=64)
def _exp_weights(w):
    """Cached exponential weights that sum to 1."""
    wgt = np.exp(np.linspace(-2, 0, w))
    return wgt / wgt.sum()

def _simple_ma(arr, w):
    """Uniform moving‑average."""
    if w == 1:
        return arr.copy()
    return np.convolve(arr, np.ones(w) / w, mode="valid")

def _weighted_ma(arr, w):
    """Exponentially weighted moving‑average."""
    if w == 1:
        return arr.copy()
    # reverse weights for zero‑phase alignment
    return np.convolve(arr, _exp_weights(w)[::-1], mode="valid")

def _median_filter(arr, w):
    """Median filter using stride tricks."""
    if w == 1:
        return arr.copy()
    half = w // 2
    padded = np.pad(arr, (half, half), mode="edge")
    shape = (arr.size, w)
    strides = (arr.strides[0], arr.strides[0])
    windows = np.lib.stride_tricks.as_strided(padded, shape=shape, strides=strides)
    return np.median(windows, axis=1)

def _enhanced_filter(arr, w):
    """Detrend with simple MA then apply weighted smoothing."""
    if w == 1:
        return arr.copy()
    trend = _simple_ma(arr, w)
    trend_full = np.concatenate((np.full(w - 1, trend[0]), trend))
    detrended = arr - trend_full
    return _weighted_ma(detrended, w)

def _residual_variance(orig, filt):
    """Mean squared error between original and filtered (truncated)."""
    n = min(len(orig), len(filt))
    if n == 0:
        return np.inf
    return float(np.var(orig[:n] - filt[:n]))

