    """Dispatch to a filtering algorithm; falls back to auto‑selection."""
    arr = _to_array(input_signal)
    w = int(window_size)
    if w <= 0:
        raise ValueError(f"window_size must be positive, got {window_size}")
    if arr.size < w:
        raise ValueError(f"Input length ({arr.size}) < window_size ({w})")

    alg = (algorithm_type or "enhanced").lower()
    if alg == "adaptive":
        return _simple_ma(arr, w)
    if alg == "weighted":
        return _weighted_ma(arr, w)
    if alg == "median":
        return _median_filter(arr, w)
    if alg == "enhanced":
        return _enhanced_filter(arr, w)

    # Auto‑selection: evaluate all candidates and pick the one with lowest residual variance.
    candidates = {
        "adaptive": _simple_ma(arr, w),
        "weighted": _weighted_ma(arr, w),
        "median": _median_filter(arr, w),
        "enhanced": _enhanced_filter(arr, w),
    }
    # Discard empty results
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
        return np.empty(0, dtype=float)

    best_key = min(candidates, key=lambda k: _residual_variance(arr, candidates[k]))
