def _to_numpy(x):
    return np.asarray(x, dtype=float).ravel()


def _ma(x, w):
    x = _to_numpy(x)
    return (
        np.convolve(x, np.ones(w) / w, mode="valid")
        if w > 0 and x.size >= w
        else np.array([], float)
    )


def _weighted(x, w):
    x = _to_numpy(x)
    if w <= 0 or x.size < w:
        return np.array([], float)
    wts = np.exp(np.linspace(-2, 0, w))
    wts /= wts.sum()
    return np.convolve(x, wts, mode="valid")


def _median(x, w):
    x = _to_numpy(x)
    if w <= 0 or x.size < w:
        return np.array([], float)
    stride = x.strides[0]
    shape = (x.size - w + 1, w)
    win = np.lib.stride_tricks.as_strided(
        x, shape=shape, strides=(stride, stride)
    )
    return np.median(win, axis=1)


def _combined(x, w):
    ma = _ma(x, w)
    if ma.size == 0:
        return ma
    trend = np.concatenate((np.full(w - 1, ma[0]), ma))
    detr = _to_numpy(x) - trend
    weighted = _weighted(detr, w)
    if weighted.size == 0:
        return weighted
    return _median(weighted, w)


def _residual_variance(orig, filt):
    n = min(len(orig), len(filt))
    return float(np.var(orig[:n] - filt[:n])) if n else float("inf")


def _select_best(x, w):
    x = _to_numpy(x)
    cand = {
        "ma": _ma(x, w),
        "weighted": _weighted(x, w),
        "median": _median(x, w),
        "combined": _combined(x, w),
    }
    return min(cand.values(), key=lambda f: _residual_variance(x, f))


def process_signal(sig, w=20, algorithm_type="enhanced"):
    alg = (algorithm_type or "").lower()
    if alg in ("basic", "ma"):
        return _ma(sig, w)
    if alg == "weighted":
        return _weighted(sig, w)
    if alg == "median":
        return _median(sig, w)
    if alg in ("enhanced", "combined"):
        return _combined(sig, w)
    return _select_best(sig, w)
