def _to_array(x):
    """Convert input to a 1‑D float NumPy array."""
    return np.asarray(x, dtype=float).ravel()


def _valid(arr, w):
    """Check that window size is positive and fits the array."""
    return w > 0 and arr.size >= w


def _exp_weights(w):
    """Exponential weights (recent samples get larger weight)."""
    # weights decreasing from newest to oldest
    wts = np.exp(np.linspace(-2, 0, w))
    return wts / wts.sum()


def _simple(arr, w):
    """Uniform moving‑average (simple) filter."""
    if not _valid(arr, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return arr.copy()
    kernel = np.full(w, 1.0 / w, dtype=float)
    return np.convolve(arr, kernel, mode="valid")


def _weighted(arr, w):
    """Exponentially weighted moving‑average filter."""
    if not _valid(arr, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return arr.copy()
    # reverse weights so that the most recent sample (rightmost) gets highest weight
    wts = _exp_weights(w)[::-1]
    return np.convolve(arr, wts, mode="valid")


def _ema(arr, w):
    """Classic exponential moving average (causal)."""
    if not _valid(arr, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return arr.copy()
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(arr)
    ema[0] = arr[0]
    for i in range(1, arr.size):
        ema[i] = alpha * arr[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]


def _enhanced(arr, w):
    """Trend‑preserving filter: detrend with simple MA then apply weighted MA."""
    if not _valid(arr, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return arr.copy()
    # estimate trend with simple moving average
    trend = _simple(arr, w)
    # pad trend to original length (repeat first value for leading positions)
    trend_full = np.concatenate((np.full(w - 1, trend[0]), trend))
    detrended = arr - trend_full
    return _weighted(detrended, w)


def _residual_variance(orig, filt):
    """Mean squared error between original (truncated) and filtered signal."""
    n = min(len(orig), len(filt))
    return float(np.var(orig[:n] - filt[:n])) if n else float("inf")


def _auto_select(arr, w):
    """Pick the best filter based on lowest residual variance."""
    candidates = {
        "simple": _simple(arr, w),
        "weighted": _weighted(arr, w),
        "ema": _ema(arr, w),
        "enhanced": _enhanced(arr, w),
    }
    # discard empty results
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
        return np.empty(0, dtype=float)
    best_key = min(candidates, key=lambda k: _residual_variance(arr, candidates[k]))
    return candidates[best_key]


def process_signal(sig, w=20, alg="auto"):
    """
    Dispatch to a filtering algorithm.

    Parameters
    ----------
    sig : array‑like
        Input signal.
    w : int, optional
        Window size (default 20). Must be > 0.
    alg : str, optional
        Algorithm selector (case‑insensitive):
        - "simple"
        - "weighted"
        - "ema"
        - "enhanced"
        - "auto"  (default) – chooses the best of the above.
    """
    arr = _to_array(sig)
    key = str(alg).lower()
    if key == "simple":
        return _simple(arr, w)
    if key == "weighted":
        return _weighted(arr, w)
    if key == "ema":
        return _ema(arr, w)
    if key == "enhanced":
        return _enhanced(arr, w)
    # default / auto
    return _auto_select(arr, w)
