import numpy as np
from functools import lru_cache

def _arr(x): return np.asarray(x, float)

def _valid(a, w):
    return w > 0 and a.size >= w

@lru_cache(None)
def _exp_weights(w):
    wgt = np.exp(np.linspace(-2, 0, w))
    return wgt / wgt.sum()

def adaptive(x, w=20):
    a = _arr(x)
    if not _valid(a, w): return np.empty(0, float)
    return a.copy() if w == 1 else np.convolve(a, np.ones(w) / w, "valid")

def weighted(x, w=20):
    a = _arr(x)
    if not _valid(a, w): return np.empty(0, float)
    return a.copy() if w == 1 else np.convolve(a, _exp_weights(w), "valid")

def enhanced(x, w=20):
    a = _arr(x)
    if not _valid(a, w): return np.empty(0, float)
    tr = adaptive(a, w)
    if tr.size == 0: return np.empty(0, float)
    tr_full = np.concatenate((np.full(w - 1, tr[0]), tr))
    return weighted(a - tr_full, w)

def _resvar(o, f):
    n = min(len(o), len(f))
    return float("inf") if n == 0 else float(np.var(o[:n] - f[:n]))

def _best(x, w):
    a = _arr(x)
    cand = {"basic": adaptive(a, w), "weighted": weighted(a, w), "enhanced": enhanced(a, w)}
    return min(cand.values(), key=lambda v: _resvar(a, v))

def process_signal(sig, w=20, alg="enhanced"):
    a = str(alg).lower()
    if a == "basic": return adaptive(sig, w)
    if a == "weighted": return weighted(sig, w)
    if a == "enhanced": return enhanced(sig, w)
    return _best(sig, w)

def construct_packing(items, capacity):
    if capacity <= 0: raise ValueError("capacity must be positive")
    if not items: return []
    idx_sz = sorted(enumerate(items), key=lambda t: t[1], reverse=True)
    bins, rem = [], []
    for i, sz in idx_sz:
        if sz > capacity:
            bins.append([i]); rem.append(0); continue
        placed = False
        for b, cap in enumerate(rem):
            if sz <= cap:
                bins[b].append(i); rem[b] -= sz; placed = True; break
        if not placed:
