def _trend_ma(x, w):
    """Simple moving average used for trend estimation."""
    return _ma(x, w)


def _weighted_ma(x, w):
    """Weighted moving average using a Gaussian kernel."""
    return _gaussian(x, w)


def _detrended_weighted(x, w):
    """Detrend with a simple MA then apply a weighted MA."""
    trend = _trend_ma(x, w)
    if trend.size == 0:
        return np.array([], dtype=float)
    # Extend trend to match original length
    trend_full = np.concatenate((np.full(w - 1, trend[0]), trend))
    detrended = _to_numpy(x) - trend_full
    return _weighted_ma(detrended, w)


def _double_enhanced(x, w):
    """Two‑stage enhancement: detrended weighted smoothing followed by light MA."""
    first = _detrended_weighted(x, w)
    if first.size == 0:
        return np.array([], dtype=float)
    half_w = max(1, w // 2)
    return _ma(first, half_w)


def _best_filter(x, w):
    """Select the filter yielding the lowest residual variance."""
    candidates = {
        "ma": _ma(x, w),
        "ema": _ema(x, w),
        "gauss": _gaussian(x, w),
        "median": _median(x, w),
        "weighted": _weighted_ma(x, w),
        "detrended": _detrended_weighted(x, w),
        "double": _double_enhanced(x, w),
    }
    orig = _to_numpy(x)
    # Discard empty results
    valid = {k: v for k, v in candidates.items() if v.size}
    if not valid:
        return np.array([], dtype=float)
    # Compute residual variances
    variances = {k: _residual_variance(orig, v) for k, v in valid.items()}
    # Choose the candidate with minimal variance; tie‑break by longest output
    min_var = min(variances.values())
    best_keys = [k for k, v in variances.items() if v == min_var]
    best_key = max(best_keys, key=lambda k: len(valid[k]))
    return valid[best_key]
