import numpy as np

def _to_array(x):
    return np.asarray(x, dtype=float).ravel()


def _simple_ma(x, w):
    if w <= 0 or x.size < w:
        return np.array([], dtype=float)
    return np.convolve(x, np.ones(w) / w, mode="valid")


def _weighted_ma(x, w):
    if w <= 0 or x.size < w:
        return np.array([], dtype=float)
    weights = np.exp(np.linspace(-2, 0, w))
    weights /= weights.sum()
    return np.convolve(x, weights, mode="valid")


def _ema(x, w):
    if w <= 0 or x.size < w:
        return np.array([], dtype=float)
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(x)
    ema[0] = x[0]
    for i in range(1, len(x)):
        ema[i] = alpha * x[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]


def _linear_trend(x):
    n = x.size
    if n == 0:
        return np.array([], dtype=float)
    t = np.arange(n, dtype=float)
    A = np.vstack([t, np.ones_like(t)]).T
    a, b = np.linalg.lstsq(A, x, rcond=None)[0]
    return a * t + b


def _median_filter(x, w):
    if w <= 0 or x.size < w:
        return np.array([], dtype=float)
    stride = x.strides[0]
    shape = (x.size - w + 1, w)
    windows = np.lib.stride_tricks.as_strided(x, shape=shape, strides=(stride, stride))
    return np.median(windows, axis=1)


def _combined_filter(x, w):
    """Detrend → weighted MA → add back trimmed trend."""
    if w <= 0 or x.size < w:
        return np.array([], dtype=float)
    trend = _linear_trend(x)
    detrended = x - trend
    smooth = _weighted_ma(detrended, w)
    if smooth.size == 0:
        return np.array([], dtype=float)
    trend_trim = trend[w - 1 :]
    return smooth + trend_trim


def _enhanced_filter(x, w):
    """Detrend → weighted MA → EMA → median → add back trimmed trend."""
    if w <= 0 or x.size < w:
        return np.array([], dtype=float)
    trend = _linear_trend(x)
    detrended = x - trend
    weighted = _weighted_ma(detrended, w)
    if weighted.size == 0:
        return np.array([], dtype=float)
    ema = _ema(weighted, w)
    if ema.size == 0:
        return np.array([], dtype=float)
    median = _median_filter(ema, w)
    if median.size == 0:
        return np.array([], dtype=float)
    offset = 3 * (w - 1)  # three stages each drop w‑1 samples
    trend_trim = trend[offset:]
    min_len = min(trend_trim.size, median.size)
    if min_len == 0:
        return np.array([], dtype=float)
    return median[:min_len] + trend_trim[:min_len]


def _robust_filter(x, w):
    """Alias for the enhanced filter."""
    return _enhanced_filter(x, w)


def _residual_variance(orig, filt):
    if filt.size == 0:
        return float("inf")
    n = min(len(orig), len(filt))
    if n == 0:
        return float("inf")
    var = np.var(orig[:n] - filt[:n])
    length_penalty = len(orig) / n
    return float(var * length_penalty)


def _select_best(x, w):
    """Select the filter with the lowest penalised residual variance."""
    candidates = {
        "basic": _simple_ma,
        "weighted": _weighted_ma,
        "ema": _ema,
        "combined": _combined_filter,
        "enhanced": _enhanced_filter,
        "robust": _robust_filter,
    }
    best_key = min(candidates, key=lambda k: _residual_variance(x, candidates[k](x, w)))
