    if alg == "ema":
        return _ema(x, window_size)
    if alg == "combined":
        return _combined_filter(x, window_size)
    if alg in ("enhanced", "robust"):
        return _enhanced_filter(x, window_size)
    # fallback: auto‑select best filter
    best = _select_best(x, window_size)
    return {
        "basic": _simple_ma,
        "weighted": _weighted_ma,
        "ema": _ema,
        "combined": _combined_filter,
        "enhanced": _enhanced_filter,
        "robust": _robust_filter,
    }[best](x, window_size)
