def _arr(x):
    return np.asarray(x, float)


def _ok(a, w):
    return w > 0 and a.size >= w


def adaptive(x, w=20):
    a = _arr(x)
    return np.convolve(a, np.ones(w) / w, "valid") if _ok(a, w) else np.empty(0)


def weighted(x, w=20):
    a = _arr(x)
    if not _ok(a, w):
        return np.empty(0)
    wt = np.exp(np.linspace(-2, 0, w))
    wt /= wt.sum()
    return np.convolve(a, wt, "valid")


def ema(x, w=20):
    a = _arr(x)
    if not _ok(a, w):
        return np.empty(0)
    alpha = 2.0 / (w + 1)
    e = np.empty_like(a)
    e[0] = a[0]
    for i in range(1, len(a)):
        e[i] = alpha * a[i] + (1 - alpha) * e[i - 1]
    return e[w - 1 :]


def enhanced(x, w=20):
    tr = adaptive(x, w)
    if tr.size == 0:
        return np.empty(0)
    tf = np.concatenate((np.full(w - 1, tr[0]), tr))
    return weighted(x - tf, w)


def median_filter(x, w=20):
    a = _arr(x)
    if not _ok(a, w):
        return np.empty(0)
    if w % 2 == 0:
        w += 1
    shape = (a.size - w + 1, w)
    strides = (a.strides[0], a.strides[0])
    win = np.lib.stride_tricks.as_strided(a, shape=shape, strides=strides)
    return np.median(win, axis=1)


def fft_denoise(x, w=20, keep_ratio=0.1):
    a = _arr(x)
    if a.size == 0:
        return np.empty(0)
    # Apply FFT, zero‑out low‑energy coefficients, then inverse FFT
    coeffs = np.fft.rfft(a)
    mags = np.abs(coeffs)
    k = max(1, int(len(mags) * keep_ratio))
    idx = np.argpartition(mags, -k)[-k:]
    mask = np.zeros_like(mags, dtype=bool)
    mask[idx] = True
    filtered = np.where(mask, coeffs, 0)
    denoised = np.fft.irfft(filtered, n=a.size)
    # Return same length as other filters (valid window)
    return denoised[w - 1 :] if _ok(denoised, w) else denoised


def _residual_variance(orig, filt):
    n = min(len(orig), len(filt))
    return float("inf") if n == 0 else float(np.var(orig[:n] - filt[:n]))


def _best_filter(sig, w):
    candidates = {
        "adaptive": adaptive(sig, w),
        "weighted": weighted(sig, w),
        "ema": ema(sig, w),
        "enhanced": enhanced(sig, w),
        "median": median_filter(sig, w),
        "fft": fft_denoise(sig, w),
    }
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
