import numpy as np
from functools import lru_cache


def _to_numpy(arr):
    """Convert input to a 1‑D float ndarray."""
    return np.asarray(arr, dtype=float).ravel()


def _ma(x, w):
    """Simple moving average (valid convolution)."""
    x = _to_numpy(x)
    if w <= 0 or x.size < w:
        return np.array([], dtype=float)
    if w == 1:
        return x.copy()
    return np.convolve(x, np.ones(w) / w, mode="valid")


def _ewma(x, w):
    """Classic exponential moving average (output length matches other filters)."""
    x = _to_numpy(x)
    if w <= 0 or x.size < w:
        return np.array([], dtype=float)
    if w == 1:
        return x.copy()
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(x)
    ema[0] = x[0]
    for i in range(1, len(x)):
        ema[i] = alpha * x[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]


@lru_cache(maxsize=64)
def _exp_weights(w):
    """Gaussian‑like kernel used for weighted smoothing."""
    sigma = 0.3 * w
    centre = (w - 1) / 2.0
    idx = np.arange(w)
    wts = np.exp(-0.5 * ((idx - centre) / sigma) ** 2)
    return wts / wts.sum()


def _gaussian(x, w):
    """Weighted convolution using the Gaussian‑shaped kernel."""
    x = _to_numpy(x)
    if w <= 0 or x.size < w:
        return np.array([], dtype=float)
    if w == 1:
        return x.copy()
    return np.convolve(x, _exp_weights(w), mode="valid")


def _median(x, w):
    """Median filter (valid)."""
    x = _to_numpy(x)
    if w <= 0 or x.size < w:
        return np.array([], dtype=float)
    if w == 1:
        return x.copy()
    if w % 2 == 0:
        w += 1  # ensure odd window for true median
    stride = x.strides[0]
    shape = (x.size - w + 1, w)
    windows = np.lib.stride_tricks.as_strided(x, shape=shape, strides=(stride, stride))
    return np.median(windows, axis=1)


def _hybrid(x, w):
    """Average of simple moving average and Gaussian smoothing."""
    ma = _ma(x, w)
    ga = _gaussian(x, w)
    if ma.size and ga.size:
        return (ma + ga) / 2.0
    return ma if ma.size else ga


def _trend_full(x, w):
    """Return a full‑length trend series based on a moving average."""
    ma = _ma(x, w)
    if ma.size == 0:
        return np.empty(0, dtype=float)
    # prepend the first average value to align lengths
    prefix = np.full(w - 1, ma[0], dtype=float)
    return np.concatenate((prefix, ma))


def _enhanced(x, w):
    """
    Detrend using a simple moving average then apply Gaussian smoothing.
    Mirrors the “enhanced” approach from earlier high‑performing programs.
    """
    x = _to_numpy(x)
    if w <= 0 or x.size < w:
        return np.array([], dtype=float)
    if w == 1:
        return x.copy()
    trend = _trend_full(x, w)
    detrended = x - trend
    return _gaussian(detrended, w)


def _residual_variance(orig, filt):
    """Variance of the residual (orig - filt) over the overlapping region."""
    n = min(len(orig), len(filt))
    if n == 0:
        return float("inf")
    return float(np.var(orig[:n] - filt[:n]))


def _best(x, w):
    """Select the filter with the smallest residual variance."""
    x = _to_numpy(x)
    candidates = {
        "ma": _ma(x, w),
        "ema": _ewma(x, w),
        "gauss": _gaussian(x, w),
        "median": _median(x, w),
        "hybrid": _hybrid(x, w),
        "enhanced": _enhanced(x, w),
    }
    # Discard empty results
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
        return np.array([], dtype=float)
    best_key = min(candidates, key=lambda k: _residual_variance(x, candidates[k]))
    return candidates[best_key]


def process_signal(sig, w=20, algorithm_type="combined"):
    """
    Dispatch to the requested algorithm or automatically select the best one.

    Supported ``algorithm_type`` values (case‑insensitive):
        - "basic", "ma"          : simple moving average
        - "ewma", "ema"          : exponential moving average
        - "gauss", "weighted"    : Gaussian weighted average
        - "median"               : median filter
        - "hybrid"               : average of MA and Gaussian
        - "enhanced"             : detrend + Gaussian smoothing
        - "combined", "auto"     : automatic selection of the best filter
        - any other value        : falls back to automatic selection
    """
    alg = (algorithm_type or "").lower()
    if alg in ("basic", "ma"):
        return _ma(sig, w)
    if alg in ("ewma", "ema"):
        return _ewma(sig, w)
    if alg in ("gauss", "weighted"):
        return _gaussian(sig, w)
    if alg == "median":
        return _median(sig, w)
    if alg == "hybrid":
        return _hybrid(sig, w)
    if alg == "enhanced":
        return _enhanced(sig, w)
    # default / auto‑selection
    return _best(sig, w)
