import numpy as np
_w={}
def _a(x):return np.asarray(x,float)
def _e(w):
    if w not in _w:
        v=np.exp(np.linspace(-2,0,w))
        _w[w]=v/v.sum()
    return _w[w]
def adaptive(x,w=20):
    a=_a(x)
    return np.convolve(a,np.ones(w)/w,mode='valid') if a.size>=w else np.empty(0)
def weighted(x,w=20):
    a=_a(x)
    return np.convolve(a,_e(w)[::-1],mode='valid') if a.size>=w else np.empty(0)
def hybrid(x,w=20):
    return weighted(adaptive(x,w),w)
def process_signal(sig,w=20,alg='enhanced'):
    a=alg.lower()
    if a=='basic':return adaptive(sig,w)
    if a=='weighted':return weighted(sig,w)
    if a=='hybrid':return hybrid(sig,w)
    # auto: variance‑based choice
    return weighted(sig,w) if np.var(_a(sig))>1 else adaptive(sig,w)
