import numpy as np
from functools import lru_cache


def _validate_signal(x, window_size, *, require_odd=False):
    """
    Convert *x* to a 1‑D float array and validate *window_size*.

    If ``require_odd`` is True and the supplied size is even, it is
    incremented to the next odd number (a common requirement for median‑type
    filters).  Empty inputs raise a ``ValueError``.
    """
    if window_size <= 0:
        raise ValueError("window_size must be positive")
    arr = np.asarray(x, dtype=float).ravel()
    if arr.ndim != 1:
        raise ValueError("Input signal must be one‑dimensional")
    if require_odd and window_size % 2 == 0:
        window_size += 1
    if arr.size == 0:
        raise ValueError("Input signal is empty")
    if arr.size < window_size:
        raise ValueError(f"Input length ({arr.size}) < window_size ({window_size})")
    return arr, window_size


def adaptive_filter(x, window_size=20):
    """Simple moving‑average filter (uniform weights)."""
    arr, w = _validate_signal(x, window_size)
    if w == 1:
        return arr.copy()
    kernel = np.full(w, 1.0 / w, dtype=float)
    return np.convolve(arr, kernel, mode="valid")


@lru_cache(maxsize=None)
def _exp_weights(size):
    """Cached right‑biased exponential weights that sum to 1."""
    w = np.exp(np.linspace(-2, 0, size))
    return w / w.sum()


def enhanced_filter_with_trend_preservation(x, window_size=20):
    """Exponential‑weighted moving average that emphasises recent samples."""
    arr, w = _validate_signal(x, window_size)
    if w == 1:
        return arr.copy()
    # reverse so newest sample gets the largest weight
    return np.convolve(arr, _exp_weights(w)[::-1], mode="valid")


def median_filter(x, window_size=20):
    """Median filter (odd window) using stride tricks for speed."""
    arr, w = _validate_signal(x, window_size, require_odd=True)
    if w == 1:
        return arr.copy()
    shape = (arr.size - w + 1, w)
    strides = (arr.strides[0], arr.strides[0])
    windows = np.lib.stride_tricks.as_strided(arr, shape=shape, strides=strides)
    return np.median(windows, axis=1)


@lru_cache(maxsize=None)
def _gaussian_kernel(size):
    """Cached Gaussian kernel (odd size, sum = 1)."""
    if size % 2 == 0:
        size += 1
    x = np.arange(size) - (size - 1) / 2.0
    sigma = size / 6.0
    g = np.exp(-0.5 * (x / sigma) ** 2)
    return g / g.sum()


def gaussian_filter(x, window_size=20):
    """Gaussian smoothing filter."""
    arr, w = _validate_signal(x, window_size)
    return np.convolve(arr, _gaussian_kernel(w), mode="valid")


@lru_cache(maxsize=None)
def _savgol_coeffs(window_size, polyorder):
    """Cached Savitzky‑Golay convolution coefficients."""
    if polyorder >= window_size:
        raise ValueError("polyorder must be less than window_size")
    half = window_size // 2
    A = np.vander(np.arange(-half, half + 1), N=polyorder + 1, increasing=True)
    return np.linalg.pinv(A)[0]


def savgol_filter(x, window_size=20, polyorder=2):
    """Savitzky‑Golay filter (polynomial smoothing)."""
    arr, w = _validate_signal(x, window_size)
    coeffs = _savgol_coeffs(w, polyorder)
    return np.convolve(arr, coeffs, mode="valid")


def trimmed_mean_filter(x, window_size=20, trim_ratio=0.1):
    """Trimmed‑mean filter – discards a fraction of extreme values per window."""
    arr, w = _validate_signal(x, window_size)
    k = int(trim_ratio * w)
    if k * 2 >= w:  # nothing left after trimming
        raise ValueError("trim_ratio too large for given window_size")
    shape = (arr.size - w + 1, w)
    strides = (arr.strides[0], arr.strides[0])
    windows = np.lib.stride_tricks.as_strided(arr, shape=shape, strides=strides)
    sorted_win = np.sort(windows, axis=1)
    trimmed = sorted_win[:, k : w - k]
    return trimmed.mean(axis=1)


def process_signal(input_signal, window_size=20, algorithm_type="enhanced", **kwargs):
    """
    Dispatch to a filtering algorithm.

    Supported ``algorithm_type`` (case‑insensitive):
        enhanced, adaptive, median, gaussian, savgol, trimmed, hybrid
    """
    algo = algorithm_type.lower()
    if algo == "enhanced":
        return enhanced_filter_with_trend_preservation(input_signal, window_size)
    if algo == "adaptive":
        return adaptive_filter(input_signal, window_size)
    if algo == "median":
        return median_filter(input_signal, window_size)
    if algo == "gaussian":
        return gaussian_filter(input_signal, window_size)
    if algo == "savgol":
        return savgol_filter(input_signal, window_size, polyorder=kwargs.get("polyorder", 2))
    if algo == "trimmed":
        return trimmed_mean_filter(input_signal, window_size, trim_ratio=kwargs.get("trim_ratio", 0.1))
    if algo == "hybrid":
        # First apply a simple moving average, then smooth the result if possible.
        interim = adaptive_filter(input_signal, window_size)
        # ``enhanced_filter_with_trend_preservation`` requires the signal length
        # to be at least ``window_size``; if the interim result is shorter, just
        # return it.
        if interim.size < window_size:
            return interim
        return enhanced_filter_with_trend_preservation(interim, window_size)
    raise ValueError(f"Unsupported algorithm_type: {algorithm_type}")
