import numpy as np
from functools import lru_cache

def _to_arr(x, w):
    a = np.asarray(x, float)
    if a.ndim != 1:
        raise ValueError("signal must be 1‑D")
    if w <= 0:
        raise ValueError("window must be positive")
    if a.size < w:
        raise ValueError("signal shorter than window")
    return a

@lru_cache(None)
def _exp_weights(w):
    e = np.exp(np.linspace(-2, 0, w))
    return e / e.sum()

def _adaptive(a, w):
    return a.copy() if w == 1 else np.convolve(a, np.ones(w) / w, mode="valid")

def _weighted(a, w):
    return a.copy() if w == 1 else np.convolve(a, _exp_weights(w)[::-1], mode="valid")

def _enhanced(a, w):
    if w == 1:
        return a.copy()
    trend = _adaptive(a, w)
    if trend.size == 0:
        return np.empty(0, float)
    full = np.concatenate((np.full(w - 1, trend[0]), trend))
    return _weighted(a - full, w)

def _residual_variance(orig, filt):
    n = min(len(orig), len(filt))
    return np.var(orig[:n] - filt[:n]) if n else np.inf

def _select_best(x, w):
    a = _to_arr(x, w)
    candidates = {
        "adaptive": _adaptive(a, w),
        "weighted": _weighted(a, w),
        "enhanced": _enhanced(a, w),
    }
    return min(candidates.values(), key=lambda c: _residual_variance(a, c))

def process_signal(sig, w=20, alg="enhanced"):
    a = _to_arr(sig, w)
    alg = alg.lower()
    if alg == "adaptive":
        return _adaptive(a, w)
    if alg == "weighted":
        return _weighted(a, w)
    if alg == "enhanced":
        return _enhanced(a, w)
    if alg == "auto":
        return _select_best(sig, w)
    return _enhanced(a, w)
