import numpy as np
from functools import lru_cache

def _to_array(x):
    """Convert input to 1‑D float array."""
    return np.asarray(x, dtype=float).ravel()

def _valid(arr, w):
    """Validate window size against array."""
    return isinstance(w, int) and w > 0 and arr.size >= w

@lru_cache(maxsize=None)
def _exp_weights(w):
    """Exponential weights that sum to 1 (right‑biased)."""
    w = int(w)
    e = np.exp(np.linspace(-2, 0, w))
    return e / e.sum()

def adaptive(x, w=20):
    """Simple moving‑average filter."""
    a = _to_array(x)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    return np.convolve(a, np.ones(w) / w, mode="valid")

def weighted(x, w=20):
    """Exponential‑weighted moving average."""
    a = _to_array(x)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    wts = _exp_weights(w)
    return np.convolve(a, wts, mode="valid")

def median(x, w=21):
    """Median filter (odd window size)."""
    a = _to_array(x)
    if w % 2 == 0:
        w += 1
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    stride = a.strides[0]
    windows = np.lib.stride_tricks.as_strided(
        a, shape=(a.size - w + 1, w), strides=(stride, stride)
    )
    return np.median(windows, axis=1)

def _residual_variance(orig, filt):
    """Variance of the residual (orig‑filt) over overlapping region."""
    n = min(len(orig), len(filt))
    if n == 0:
        return np.inf
    return float(np.var(orig[:n] - filt[:n]))

def _select_best(x, w):
    """Choose the filter with the smallest residual variance."""
    candidates = {
        "adaptive": adaptive(x, w),
        "weighted": weighted(x, w),
        "median": median(x, w),
    }
    # Discard empty results
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
        return np.empty(0, dtype=float)
    best_key = min(candidates, key=lambda k: _residual_variance(_to_array(x), candidates[k]))
    return candidates[best_key]

def process_signal(sig, w=20, alg="enhanced"):
    """
    Dispatch to the requested filtering algorithm.

    Supported ``alg`` values (case‑insensitive):
        - "adaptive"
        - "weighted" / "enhanced"
        - "median"
        - "auto" – automatically select the best filter.
    """
    key = str(alg).lower()
    if key == "auto":
        return _select_best(sig, w)
    if key == "adaptive":
        return adaptive(sig, w)
    if key in ("weighted", "enhanced"):
        return weighted(sig, w)
    if key == "median":
        return median(sig, w)
    raise ValueError(f"Unsupported algorithm_type: {alg}")
