import numpy as np

def _arr(x):
    return np.asarray(x, float).ravel()


def _ma(a, w):
    return (
        np.convolve(a, np.ones(w) / w, "valid")
        if w > 0 and a.size >= w
        else np.empty(0, float)
    )


def _ema(a, w):
    if w <= 0 or a.size < w:
        return np.empty(0, float)
    alpha = 2.0 / (w + 1)
    e = np.empty_like(a)
    e[0] = a[0]
    for i in range(1, len(a)):
        e[i] = alpha * a[i] + (1 - alpha) * e[i - 1]
    return e[w - 1 :]


def _weighted(a, w):
    if w <= 0 or a.size < w:
        return np.empty(0, float)
    idx = np.arange(w)
    sigma = 0.3 * w
    centre = (w - 1) / 2.0
    wts = np.exp(-0.5 * ((idx - centre) / sigma) ** 2)
    wts /= wts.sum()
    return np.convolve(a, wts, "valid")


def _detrended(a, w):
    tr = _ma(a, w)
    if tr.size == 0:
        return np.empty(0, float)
    full = np.concatenate((np.full(w - 1, tr[0]), tr))
    return _weighted(a - full, w)


def _resvar(o, f):
    n = min(len(o), len(f))
    return float(np.var(o[:n] - f[:n])) if n else float("inf")


def _best(x, w):
    a = _arr(x)
    cand = {
        "ma": _ma(a, w),
        "ema": _ema(a, w),
        "weighted": _weighted(a, w),
        "detrended": _detrended(a, w),
    }
    return min(cand.values(), key=lambda v: _resvar(a, v))


