def construct_packing(
    items: Iterable[float],
    containers: Union[float, Iterable[float]],
) -> List[List[int]]:
    """
    First‑Fit Decreasing bin‑packing.

    Parameters
    ----------
    items : iterable of non‑negative numbers (item sizes).
    containers : either a single positive number (uniform capacity) or an iterable of
                 positive numbers (capacities of available containers).

    Returns
    -------
    List of bins, each bin is a list of original item indices (sorted within the bin).
    """
    # Normalise inputs
    items_list = list(items)
    if not items_list:
        return []

    # Validate item sizes and pair with original indices
    indexed_items = []
    for idx, size in enumerate(items_list):
        if size < 0:
            raise ValueError("item sizes must be non‑negative")
        indexed_items.append((idx, float(size)))

    # Determine container capacities
    if isinstance(containers, (int, float)):
        if containers <= 0:
            raise ValueError("container capacity must be positive")
        uniform_capacity = float(containers)
        capacity_pool = None
    else:
        capacity_pool = [float(c) for c in containers]
        if not capacity_pool:
            raise ValueError("containers iterable is empty")
        if any(c <= 0 for c in capacity_pool):
            raise ValueError("all container capacities must be positive")
        uniform_capacity = None

    # Sort items by decreasing size for First‑Fit Decreasing
    indexed_items.sort(key=lambda t: t[1], reverse=True)

    bins: List[List[int]] = []
    remaining: List[float] = []  # remaining capacity per bin

    # Helper to obtain a new bin capacity
    def new_capacity() -> float:
        if uniform_capacity is not None:
