        # use next supplied capacity if available, else reuse the largest one seen
        if capacity_pool:
            return capacity_pool.pop(0)
        return max(capacity_pool) if capacity_pool else 0.0

    for idx, size in indexed_items:
        # Oversized items get their own bin (cannot be combined)
        max_cap = uniform_capacity if uniform_capacity is not None else max(capacity_pool) if capacity_pool else size
        if size > max_cap:
            bins.append([idx])
            remaining.append(0.0)
            continue

