        for b_idx, cap in enumerate(remaining):
            if size <= cap:
                bins[b_idx].append(idx)
                remaining[b_idx] -= size
                placed = True
                break
        if not placed:
            cap = new_capacity()
            bins.append([idx])
            remaining.append(cap - size)

    # Ensure deterministic ordering inside each bin
    for b in bins:
