import numpy as np

def _to_numpy(x: object) -> np.ndarray:
    """Convert input to a 1‑D float NumPy array."""
    return np.asarray(x, dtype=float).ravel()


def _validate_window(w: int) -> None:
    """Validate that window size is a positive integer."""
    if not isinstance(w, int) or w <= 0:
        raise ValueError("window size must be a positive integer")


def adaptive_filter(x: object, w: int) -> np.ndarray:
    """Simple moving‑average filter (valid convolution)."""
    _validate_window(w)
    x_arr = _to_numpy(x)
    if x_arr.size < w:
        return np.array([], dtype=float)
    return np.convolve(x_arr, np.ones(w) / w, mode="valid")


def weighted_filter(x: object, w: int) -> np.ndarray:
    """Exponential‑weighted moving average (valid convolution)."""
    _validate_window(w)
    x_arr = _to_numpy(x)
    if x_arr.size < w:
        return np.array([], dtype=float)
    weights = np.exp(np.linspace(-2, 0, w))
    weights /= weights.sum()
    return np.convolve(x_arr, weights, mode="valid")


def ema_filter(x: object, w: int) -> np.ndarray:
    """Classic exponential moving average (output length = len(x)‑w+1)."""
    _validate_window(w)
    x_arr = _to_numpy(x)
    if x_arr.size < w:
        return np.array([], dtype=float)
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(x_arr)
    ema[0] = x_arr[0]
    for i in range(1, len(x_arr)):
        ema[i] = alpha * x_arr[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]


def median_filter(x: object, w: int = 20) -> np.ndarray:
    """Sliding‑window median filter (valid output)."""
    _validate_window(w)
    x_arr = _to_numpy(x)
    if x_arr.size < w:
        return np.array([], dtype=float)
    stride = x_arr.strides[0]
    shape = (x_arr.size - w + 1, w)
    windows = np.lib.stride_tricks.as_strided(
        x_arr, shape=shape, strides=(stride, stride)
    )
    return np.median(windows, axis=1)


def _linear_detrend(x: object) -> np.ndarray:
    """Remove a linear trend via least‑squares fit."""
    x_arr = _to_numpy(x)
    n = x_arr.size
    if n == 0:
        return x_arr
    t = np.arange(n, dtype=float)
    a, b = np.linalg.lstsq(
        np.vstack([t, np.ones_like(t)]).T, x_arr, rcond=None
    )[0]
    return x_arr - (a * t + b)


def enhanced_filter_with_trend_preservation(x: object, w: int) -> np.ndarray:
    """
    Detrend the signal linearly, then apply an exponential‑weighted moving average.
    """
    _validate_window(w)
    x_arr = _to_numpy(x)
    if x_arr.size < w:
        return np.array([], dtype=float)
    detrended = _linear_detrend(x_arr)
    return weighted_filter(detrended, w)


def combined_filter(x: object, w: int = 20) -> np.ndarray:
    """
    Pipeline: linear detrend → weighted moving average → median smoothing.
    Returns an empty array if any stage fails.
    """
    _validate_window(w)
    x_arr = _to_numpy(x)
    if x_arr.size < w:
        return np.array([], dtype=float)

    detrended = _linear_detrend(x_arr)
    weighted = weighted_filter(detrended, w)
    if weighted.size == 0:
        return np.array([], dtype=float)

    return median_filter(weighted, w)


def _residual_variance(orig: np.ndarray, filtered: np.ndarray) -> float:
    """
    Compute variance of (orig – filtered) over the overlapping region.
    Returns ``inf`` for empty overlap to penalise invalid solutions.
    """
    n = min(len(orig), len(filtered))
    if n == 0:
        return float("inf")
    return float(np.var(orig[:n] - filtered[:n]))


def _select_best_filter(x: object, w: int) -> np.ndarray:
    """
    Evaluate all available filters and return the output with the lowest residual variance.
    Deterministic tie‑breaking is performed by alphabetical order of filter names.
    """
    _validate_window(w)
    x_arr = _to_numpy(x)
    candidates = {
        "basic": adaptive_filter(x_arr, w),
        "weighted": weighted_filter(x_arr, w),
        "ema": ema_filter(x_arr, w),
        "enhanced": enhanced_filter_with_trend_preservation(x_arr, w),
        "combined": combined_filter(x_arr, w),
    }
    best_name = min(
        candidates,
        key=lambda k: (_residual_variance(x_arr, candidates[k]), k)
    )
    return candidates[best_name]


def process_signal(sig: object, w: int = 20, alg: str = "combined") -> np.ndarray:
    """
    Dispatch to the requested filtering algorithm.

    Supported ``alg`` values:
        - "basic": simple moving average
        - "weighted": exponential weighted moving average
        - "ema": classic exponential moving average
        - "enhanced": linear detrend + weighted filter
        - "combined": linear detrend → weighted → median
        - any other value: automatic selection of the best filter
    """
    if alg == "basic":
        return adaptive_filter(sig, w)
    if alg == "weighted":
        return weighted_filter(sig, w)
    if alg == "ema":
        return ema_filter(sig, w)
    if alg == "enhanced":
        return enhanced_filter_with_trend_preservation(sig, w)
    if alg == "combined":
        return combined_filter(sig, w)

    # Fallback: automatic selection
    return _select_best_filter(sig, w)
