import numpy as np

def _arr(x):
    """Convert input to 1‑D float NumPy array."""
    return np.asarray(x, float).ravel()


def _ma(a, w, k):
    """Convolution helper for moving‑average‑style filters."""
    if w <= 0 or a.size < w:
        return np.empty(0, float)
    return np.convolve(a, k, mode="valid")


def adaptive(x, w=20):
    """Uniform moving average (basic filter)."""
    a = _arr(x)
    return _ma(a, w, np.ones(w) / w)


def weighted(x, w=20):
    """Exponentially weighted moving average."""
    a = _arr(x)
    wt = np.exp(np.linspace(-2, 0, w))
    wt /= wt.sum()
    return _ma(a, w, wt)


def ema(x, w=20):
    """Classic exponential moving average (recursive)."""
    a = _arr(x)
    if w <= 0 or a.size < w:
        return np.empty(0, float)
    alpha = 2.0 / (w + 1)
    out = np.empty_like(a)
    out[0] = a[0]
    for i in range(1, len(a)):
        out[i] = alpha * a[i] + (1 - alpha) * out[i - 1]
    return out[w - 1 :]


def enhanced(x, w=20):
    """Detrended weighted smoothing."""
    a = _arr(x)
    t = adaptive(a, w)
    if t.size == 0:
        return np.empty(0, float)
    t_full = np.concatenate((np.full(w - 1, t[0]), t))
    return weighted(a - t_full, w)


def _gaussian_kernel(w):
    """Create a Gaussian kernel with sigma ≈ 0.3·w."""
    sigma = max(0.3 * w, 0.1)
    centre = (w - 1) / 2.0
    i = np.arange(w, dtype=float)
    k = np.exp(-0.5 * ((i - centre) / sigma) ** 2)
    k /= k.sum()
    return k


def gaussian(x, w=20):
    """Moving average with a Gaussian kernel."""
    a = _arr(x)
    k = _gaussian_kernel(w)
    return _ma(a, w, k)


def median(x, w=20):
    """Median filter – robust to spikes/outliers."""
    a = _arr(x)
    if w <= 0 or a.size < w:
        return np.empty(0, float)
    stride = a.strides[0]
    shape = (a.size - w + 1, w)
    windows = np.lib.stride_tricks.as_strided(a, shape=shape, strides=(stride, stride))
    return np.median(windows, axis=1)


def double_enhanced(x, w=20):
    """
    Two‑stage enhancement:
    1. Detrended weighted smoothing (enhanced).
    2. Light uniform smoothing with a smaller window (≈ half size).
    """
    first = enhanced(x, w)
    if first.size == 0:
        return np.empty(0, float)
    half = max(1, w // 2)
    return adaptive(first, half)


def _resvar(o, f):
    """Mean‑squared residual variance between original and filtered signals."""
    n = min(len(o), len(f))
    return float("inf") if n == 0 else float(np.var(o[:n] - f[:n]))


def process_signal(sig, w=20, alg="enhanced"):
    """
    Dispatch to a filtering algorithm.
    Supported ``alg`` values (case‑insensitive):
        basic / adaptive, weighted, ema, enhanced,
        gaussian, median, double.
    Any other value triggers automatic selection of the best filter.
    """
    alg = (alg or "").lower()
    dispatch = {
        "basic": adaptive,
        "adaptive": adaptive,
        "weighted": weighted,
        "ema": ema,
        "enhanced": enhanced,
        "gaussian": gaussian,
        "median": median,
        "double": double_enhanced,
    }

    if alg in dispatch:
        return dispatch[alg](sig, w)

    a = _arr(sig)
    # Compute each candidate once and keep those that produce output
    candidates = {name: fn(a, w) for name, fn in dispatch.items() if fn(a, w).size}
    if not candidates:
        return np.empty(0, float)

    # Choose the filter with the lowest residual variance
    best_name = min(candidates, key=lambda k: _resvar(a, candidates[k]))
    return candidates[best_name]
