    n = min(len(orig), len(filt))
    return float(np.var(orig[:n] - filt[:n])) if n else float("inf")

def _best_filter(x, w):
    a = _to_arr(x)
    cand = {
        "ma": adaptive_filter(a, w),
        "w": weighted_filter(a, w),
        "ema": ema_filter(a, w),
        "enh": enhanced_filter(a, w),
    }
    cand = {k: v for k, v in cand.items() if v.size}
    if not cand:
        return np.empty(0, dtype=float)
    return min(cand.values(), key=lambda f: _residual_var(a, f))

