import numpy as np
from functools import lru_cache

def _to_array(x):
    """Convert input to 1‑D float ndarray."""
    return np.asarray(x, dtype=float).ravel()

def _valid(arr, w):
    """Check that window size is positive and fits the array."""
    return w > 0 and arr.size >= w

@lru_cache(maxsize=None)
def _exp_weights(w: int) -> np.ndarray:
    """Right‑biased exponential weights that sum to 1."""
    wts = np.exp(np.linspace(-2, 0, w))
    return wts / wts.sum()

@lru_cache(maxsize=None)
def _gaussian_weights(w: int) -> np.ndarray:
    """Zero‑phase Gaussian weights."""
    idx = np.arange(w)
    mid = (w - 1) / 2.0
    sigma = max(w / 6.0, 1e-8)
    g = np.exp(-0.5 * ((idx - mid) / sigma) ** 2)
    return g / g.sum()

def _simple(x, w):
    """Simple moving‑average (causal)."""
    a = _to_array(x)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    return np.convolve(a, np.ones(w) / w, mode="valid")

def _weighted(x, w):
    """Exponential‑weighted moving average."""
    a = _to_array(x)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    return np.convolve(a, _exp_weights(w)[::-1], mode="valid")

def _ema(x, w):
    """Classic EMA (output length matches other filters)."""
    a = _to_array(x)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    alpha = 2.0 / (w + 1)
    e = np.empty_like(a)
    e[0] = a[0]
    for i in range(1, len(a)):
        e[i] = alpha * a[i] + (1 - alpha) * e[i - 1]
    return e[w - 1 :]

def _gaussian(x, w):
    """Zero‑phase Gaussian weighted moving average."""
    a = _to_array(x)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    return np.convolve(a, _gaussian_weights(w)[::-1], mode="valid")

def _median(x, w):
    """Median filter (odd window enforced)."""
    if w % 2 == 0:
        w += 1
    a = _to_array(x)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    shape = (a.size - w + 1, w)
    strides = (a.strides[0], a.strides[0])
    windows = np.lib.stride_tricks.as_strided(a, shape=shape, strides=strides)
    return np.median(windows, axis=1)

def _hybrid(x, w):
    """Median followed by exponential‑weighted smoothing."""
    return _weighted(_median(x, w), w)

def _enhanced(x, w):
    """Detrend with simple MA then apply exponential‑weighted filter."""
    trend = _simple(x, w)
    if trend.size == 0:
        return np.empty(0, dtype=float)
    # Pad trend to original length (edge repeat)
    trend_full = np.concatenate((np.full(w - 1, trend[0]), trend))
    detrended = _to_array(x) - trend_full
    return _weighted(detrended, w)

def _residual_variance(orig, filt):
    """Variance of residual over the overlapping region."""
    n = min(len(orig), len(filt))
    if n == 0:
        return np.inf
    return float(np.var(orig[:n] - filt[:n]))

def _select_best(x, w):
    """Choose filter with minimal residual variance."""
    a = _to_array(x)
    candidates = {
        "simple": _simple(a, w),
        "weighted": _weighted(a, w),
        "ema": _ema(a, w),
        "gaussian": _gaussian(a, w),
        "median": _median(a, w),
        "hybrid": _hybrid(a, w),
        "enhanced": _enhanced(a, w),
    }
    # Discard empty results
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
        return np.empty(0, dtype=float)
    best_key = min(candidates, key=lambda k: _residual_variance(a, candidates[k]))
    return candidates[best_key]

def process_signal(sig, w=20, alg="enhanced"):
    """
    Dispatch to the requested filtering algorithm.

    Parameters
    ----------
    sig : array‑like
        Input signal.
    w : int, optional
        Window size (default 20).
    alg : str, optional
        Algorithm name. Supported names:
        "adaptive", "weighted", "ema", "gaussian",
        "median", "hybrid", "enhanced", "auto".
        Unknown names fall back to auto‑selection.
    """
    key = str(alg).lower()
    a = _to_array(sig)

    if key == "adaptive":
        return _simple(a, w)
    if key == "weighted":
        return _weighted(a, w)
    if key == "ema":
        return _ema(a, w)
    if key == "gaussian":
        return _gaussian(a, w)
    if key == "median":
        return _median(a, w)
    if key == "hybrid":
        return _hybrid(a, w)
    if key == "enhanced":
        return _enhanced(a, w)
    # "auto" or any unsupported name
    return _select_best(a, w)
