import numpy as np
from functools import lru_cache

def _val(x, w):
    w = int(w)
    if w <= 0:
        raise ValueError
    a = np.asarray(x, float)
    if a.ndim != 1:
        raise ValueError
    if a.size < w:
        raise ValueError
    return a, w

@lru_cache(None)
def _exp(n):
    w = np.exp(np.linspace(-2, 0, n))
    return w / w.sum()

def adaptive(x, w=20):
    a, w = _val(x, w)
    if w == 1:
        return a.copy()
    return np.convolve(a, np.ones(w) / w, "valid")

def weighted(x, w=20):
    a, w = _val(x, w)
    if w == 1:
        return a.copy()
    return np.convolve(a, _exp(w)[::-1], "valid")

def median(x, w=20):
    a, w = _val(x, w)
    if w % 2 == 0:
        w += 1
    if w == 1:
        return a.copy()
    s = (a.size - w + 1, w)
    st = (a.strides[0], a.strides[0])
    return np.median(np.lib.stride_tricks.as_strided(a, s, st), axis=1)

def enhanced(x, w=20):
    a, w = _val(x, w)
    if w == 1:
        return a.copy()
    tr = adaptive(a, w)
    if tr.size == 0:
        return np.empty(0, dtype=float)
    full = np.concatenate((np.full(w - 1, tr[0]), tr))
    return np.convolve(a - full, _exp(w)[::-1], "valid")

def fft_denoise(x, keep=0.1):
    a = np.asarray(x, float)
    if a.size == 0:
        return a
    c = np.fft.rfft(a)
    m = np.abs(c)
    k = max(1, int(len(m) * keep))
    idx = np.argpartition(m, -k)[-k:]
    mask = np.zeros_like(m, bool)
    mask[idx] = True
    return np.fft.irfft(np.where(mask, c, 0), n=a.size)

def _resid(o, f):
    n = min(len(o), len(f))
    return float("inf") if n == 0 else float(np.var(o[:n] - f[:n]))

def _best(sig, w):
    raw = np.asarray(sig, float)
    cand = {
        "a": adaptive(raw, w),
        "e": enhanced(raw, w),
        "m": median(raw, w),
        "f": fft_denoise(raw),
    }
    cand = {k: v for k, v in cand.items() if v.size}
    return min(cand, key=lambda k: _resid(raw, cand[k])) if cand else np.empty(0, float)

def process_signal(sig, w=20, alg="enhanced"):
    a = alg.lower()
    if a == "adaptive":
        return adaptive(sig, w)
    if a == "enhanced":
        return enhanced(sig, w)
    if a == "median":
        return median(sig, w)
    if a == "fft":
        return fft_denoise(sig)
    return _best(sig, w)
