import numpy as np
from functools import lru_cache

def _prepare(x, w):
    """Validate and convert input to a 1‑D float ndarray."""
    if not isinstance(w, int) or w <= 0:
        raise ValueError("window size must be a positive integer")
    arr = np.asarray(x, dtype=float).ravel()
    if arr.size < w:
        return np.empty(0, dtype=float), w
    return arr, w

@lru_cache(maxsize=32)
def _gauss_weights(w):
    """Gaussian‑like kernel for weighted smoothing."""
    sigma = 0.3 * w
    centre = (w - 1) / 2.0
    idx = np.arange(w)
    wts = np.exp(-0.5 * ((idx - centre) / sigma) ** 2)
    return wts / wts.sum()

def _ma(arr, w):
    if w <= 0 or arr.size < w:
        return np.empty(0, dtype=float)
    return np.convolve(arr, np.ones(w) / w, mode="valid")

def _ema(arr, w):
    if w <= 0 or arr.size < w:
        return np.empty(0, dtype=float)
    alpha = 2.0 / (w + 1)
    out = np.empty_like(arr)
    out[0] = arr[0]
    for i in range(1, len(arr)):
        out[i] = alpha * arr[i] + (1 - alpha) * out[i - 1]
    return out[w - 1 :]

def _gauss(arr, w):
    if w <= 0 or arr.size < w:
        return np.empty(0, dtype=float)
    return np.convolve(arr, _gauss_weights(w), mode="valid")

def _median(arr, w):
    if w <= 0 or arr.size < w:
        return np.empty(0, dtype=float)
    stride = arr.strides[0]
    shape = (arr.size - w + 1, w)
    windows = np.lib.stride_tricks.as_strided(arr, shape=shape, strides=(stride, stride))
    return np.median(windows, axis=1)

def _detrended_gauss(arr, w):
    """Trend‑preserving Gaussian smoothing."""
    trend = _ma(arr, w)                     # length n‑w+1
    pad = w - 1
    trend_full = np.concatenate((np.full(pad, trend[0]), trend))
    detrended = arr - trend_full
    smoothed = _gauss(detrended, w)        # length n‑w+1
    return smoothed + trend

def _residual_variance(orig, filt):
    if filt.size == 0:
        return np.inf
    n = min(len(orig), len(filt))
    return float(np.var(orig[:n] - filt[:n]))

def _best(arr, w):
    candidates = {
        "ma": _ma(arr, w),
        "ema": _ema(arr, w),
        "gauss": _gauss(arr, w),
        "median": _median(arr, w),
        "enhanced": _detrended_gauss(arr, w),
    }
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
        return np.empty(0, dtype=float)
    best_key = min(candidates, key=lambda k: _residual_variance(arr, candidates[k]))
    return candidates[best_key]

def adaptive_filter(x, w=20):
    """Alias for simple moving‑average."""
    arr, ws = _prepare(x, w)
    if arr.size == 0:
        return arr
    return _ma(arr, ws)

def process_signal(sig, w=20, algorithm_type="combined"):
    """Dispatch to a filtering algorithm.

    Supported ``algorithm_type`` values (case‑insensitive):
        basic / ma          – simple moving average
        ewma / ema          – exponential moving average
        weighted / gauss    – Gaussian weighted average
        median              – median filter
        enhanced / detrend – trend‑preserving Gaussian smoothing
        combined / auto     – automatic selection of best filter
    """
    arr, ws = _prepare(sig, w)
    if arr.size == 0:
        return arr
    alg = (algorithm_type or "").lower()
    if alg in ("basic", "ma"):
        return _ma(arr, ws)
    if alg in ("ewma", "ema"):
        return _ema(arr, ws)
    if alg in ("weighted", "gauss"):
        return _gauss(arr, ws)
    if alg == "median":
