def process_signal(sig, w=20, algorithm_type="combined"):
    """Dispatch to a filtering algorithm.

    Supported ``algorithm_type`` values (case‑insensitive):
        basic / ma          – simple moving average
        ewma / ema          – exponential moving average
        weighted / gauss    – Gaussian weighted average
        median              – median filter
        enhanced / detrend – trend‑preserving Gaussian smoothing
        combined / auto     – automatic selection of best filter
    """
    arr, ws = _prepare(sig, w)
    if arr.size == 0:
        return arr
    alg = (algorithm_type or "").lower()
    if alg in ("basic", "ma"):
        return _ma(arr, ws)
    if alg in ("ewma", "ema"):
        return _ema(arr, ws)
    if alg in ("weighted", "gauss"):
        return _gauss(arr, ws)
    if alg == "median":
        return _median(arr, ws)
    if alg in ("enhanced", "detrend"):
        return _detrended_gauss(arr, ws)
    return _best(arr, ws)
