import numpy as np

def _to_arr(x):
    """Convert input to 1‑D float NumPy array."""
    return np.asarray(x, dtype=float).ravel()


def _empty():
    return np.empty(0, dtype=float)


def adaptive(x, w=20):
    a = _to_arr(x)
    if w <= 0 or a.size < w:
        return _empty()
    return np.convolve(a, np.ones(w) / w, mode="valid")


def weighted(x, w=20):
    a = _to_arr(x)
    if w <= 0 or a.size < w:
        return _empty()
    wt = np.exp(np.linspace(-2, 0, w))
    wt /= wt.sum()
    return np.convolve(a, wt, mode="valid")


def ema(x, w=20):
    a = _to_arr(x)
    if w <= 0 or a.size < w:
        return _empty()
    alpha = 2.0 / (w + 1)
    e = np.empty_like(a)
    e[0] = a[0]
    for i in range(1, len(a)):
        e[i] = alpha * a[i] + (1 - alpha) * e[i - 1]
    return e[w - 1 :]


def enhanced(x, w=20):
    tr = adaptive(x, w)
    if tr.size == 0:
        return _empty()
    tf = np.concatenate((np.full(w - 1, tr[0]), tr))
    return weighted(_to_arr(x) - tf, w)


def median_filter(x, w=20):
    a = _to_arr(x)
    if w <= 0 or a.size < w:
        return _empty()
    if w % 2 == 0:
        w += 1
    shape = (a.size - w + 1, w)
    strides = (a.strides[0], a.strides[0])
    win = np.lib.stride_tricks.as_strided(a, shape=shape, strides=strides)
    return np.median(win, axis=1)


def fft_denoise(x, keep_ratio=0.1):
    a = _to_arr(x)
    n = a.size
    if n == 0:
        return _empty()
    coeffs = np.fft.rfft(a)
    mags = np.abs(coeffs)
    k = max(1, int(len(mags) * keep_ratio))
    idx = np.argpartition(mags, -k)[-k:]
    mask = np.zeros_like(mags, dtype=bool)
    mask[idx] = True
    filtered = np.where(mask, coeffs, 0)
    return np.fft.irfft(filtered, n=n)


def _var(orig, filt):
    n = min(len(orig), len(filt))
    if n == 0:
        return float("inf")
    return float(np.var(orig[:n] - filt[:n]))


def process_signal(sig, w=20, alg="enh"):
    sig_arr = _to_arr(sig)
    if w <= 0 or sig_arr.size < w:
        return _empty()

    alg = (alg or "").lower()
    dispatch = {
        "basic": adaptive,
        "adaptive": adaptive,
        "weighted": weighted,
        "ema": ema,
        "exponential": ema,
        "enh": enhanced,
        "enhanced": enhanced,
        "median": median_filter,
        "med": median_filter,
        "fft": fft_denoise,
        "spectral": fft_denoise,
    }

    if alg in dispatch:
        # fft_denoise expects keep_ratio, use default
        if dispatch[alg] is fft_denoise:
            return fft_denoise(sig_arr, keep_ratio=0.1)
        return dispatch[alg](sig_arr, w)

    # Automatic selection
    candidates = {
        "a": adaptive(sig_arr, w),
        "w": weighted(sig_arr, w),
        "e": ema(sig_arr, w),
        "h": enhanced(sig_arr, w),
        "m": median_filter(sig_arr, w),
        "f": fft_denoise(sig_arr, keep_ratio=0.1),
    }
    # Filter out empty results
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
        return _empty()
