import numpy as np
from functools import lru_cache

def _prepare(x, w):
    """Validate window and convert input to 1‑D float array."""
    if not isinstance(w, int) or w <= 0:
        raise ValueError("window_size must be a positive integer")
    arr = np.asarray(x, dtype=float).ravel()
    if arr.size == 0:
        return arr, False
    if arr.size < w:
        return np.array([], dtype=float), False
    if w == 1:
        return arr, True
    return arr, True

@lru_cache(maxsize=64)
def _gaussian_weights(w):
    sigma = 0.3 * w
    centre = (w - 1) / 2.0
    idx = np.arange(w)
    weights = np.exp(-0.5 * ((idx - centre) / sigma) ** 2)
    return weights / weights.sum()

@lru_cache(maxsize=64)
def _exp_weights(w):
    weights = np.exp(np.linspace(-2, 0, w))
    return weights / weights.sum()

def _ma(arr, w):
    return np.convolve(arr, np.ones(w) / w, mode="valid")

def _gaussian(arr, w):
    return np.convolve(arr, _gaussian_weights(w), mode="valid")

def _ema(arr, w):
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(arr)
    ema[0] = arr[0]
    for i in range(1, arr.size):
        ema[i] = alpha * arr[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]

def _weighted(arr, w):
    return np.convolve(arr, _exp_weights(w), mode="valid")

def _median(arr, w):
    stride = arr.strides[0]
    shape = (arr.size - w + 1, w)
    windows = np.lib.stride_tricks.as_strided(arr, shape=shape, strides=(stride, stride))
    return np.median(windows, axis=1)

def _residual_variance(orig, filt):
    n = min(len(orig), len(filt))
    if n == 0:
        return float("inf")
    return float(np.var(orig[:n] - filt[:n]))

def _best_filter(x, w):
    arr, ok = _prepare(x, w)
    if not ok:
        return np.array([], dtype=float)
    candidates = {
        "ma": _ma(arr, w),
        "gaussian": _gaussian(arr, w),
        "ema": _ema(arr, w),
        "weighted": _weighted(arr, w),
        "median": _median(arr, w),
    }
    best_name = min(candidates, key=lambda k: _residual_variance(arr, candidates[k]))
    best = candidates[best_name]
    return best if best.size else _ma(arr, w)

def process_signal(sig, w=20, algorithm_type="combined"):
    """
    Dispatch to a filtering algorithm.

    Supported ``algorithm_type`` values (case‑insensitive):
        - "basic", "ma": simple moving average
        - "gaussian": Gaussian weighted average
        - "ema": classic exponential moving average
        - "expweight", "exp_weight": exponential‑weighted average
        - "median": median filter
        - "auto", "combined", "enhanced": automatic best‑filter selection
        - any other value falls back to automatic selection.
