    arr, ok = _prepare(sig, w)
    if not ok:
        return np.array([], dtype=float)

    if alg in ("basic", "ma"):
        return _ma(arr, w)
    if alg == "gaussian":
        return _gaussian(arr, w)
    if alg == "ema":
        return _ema(arr, w)
