import numpy as np
from functools import lru_cache
from typing import List, Dict, Any

def _arr(x):
    return np.asarray(x, float)

def _valid(a, w):
    return w > 0 and a.size >= w

@lru_cache(None)
def _exp_weights(w):
    wgt = np.exp(np.linspace(-2, 0, w))
    return wgt / wgt.sum()

def adaptive(x, w=20):
    a = _arr(x)
    if not _valid(a, w):
        return np.empty(0, float)
    return a.copy() if w == 1 else np.convolve(a, np.ones(w) / w, "valid")

def weighted(x, w=20):
    a = _arr(x)
    if not _valid(a, w):
        return np.empty(0, float)
    return a.copy() if w == 1 else np.convolve(a, _exp_weights(w), "valid")

def enhanced(x, w=20):
    a = _arr(x)
    if not _valid(a, w):
        return np.empty(0, float)
    tr = adaptive(a, w)
    if tr.size == 0:
        return np.empty(0, float)
    tr_full = np.concatenate((np.full(w - 1, tr[0]), tr))
    return weighted(a - tr_full, w)

def _resvar(o, f):
    n = min(len(o), len(f))
    return float("inf") if n == 0 else float(np.var(o[:n] - f[:n]))

def _best(x, w):
    a = _arr(x)
    cand = {"basic": adaptive(a, w), "weighted": weighted(a, w), "enhanced": enhanced(a, w)}
    return min(cand.values(), key=lambda v: _resvar(a, v))

def process_signal(sig, w=20, alg="enhanced"):
    a = str(alg).lower()
    if a == "basic":
        return adaptive(sig, w)
    if a == "weighted":
        return weighted(sig, w)
    if a == "enhanced":
        return enhanced(sig, w)
    return _best(sig, w)

def _fits(bin_info: Dict[str, Any], weight: float, volume: float) -> bool:
    """Return True if the item fits into the bin respecting remaining capacities."""
    return weight <= bin_info["remaining_weight"] and volume <= bin_info["remaining_volume"]

def _add_to_bin(
    bin_info: Dict[str, Any], item_id: Any, weight: float, volume: float
) -> None:
    """Update bin information in‑place with a new item."""
    bin_info["items"].append(item_id)
    bin_info["total_weight"] += weight
    bin_info["total_volume"] += volume
    bin_info["remaining_weight"] -= weight
    bin_info["remaining_volume"] -= volume

def construct_packing(
    items: List[Any], max_weight: float, max_volume: float
) -> List[Dict[str, Any]]:
    """
    Greedy first‑fit‑decreasing packing for items with weight and volume.

    Args:
        items: Iterable of items. Each item can be:
            * a dict with keys ``weight`` and ``volume`` (optional ``id``)
            * a sequence/tuple where the first two elements are weight and volume
              and the third (optional) element is an identifier.
        max_weight: Positive maximum weight per bin.
        max_volume: Positive maximum volume per bin.

    Returns:
        List of bins. Each bin is a dict:
        {
            "items": [item_id, ...],
            "total_weight": float,
            "total_volume": float
        }
    """
    if max_weight <= 0 or max_volume <= 0:
        raise ValueError("max_weight and max_volume must be positive")
    if not items:
        return []

    # Normalise items to (id, weight, volume)
    normalized: List[tuple] = []
    for idx, it in enumerate(items):
        if isinstance(it, dict):
            w = float(it.get("weight", 0))
            v = float(it.get("volume", 0))
            item_id = it.get("id", idx)
        elif isinstance(it, (list, tuple)) and len(it) >= 2:
            w = float(it[0])
            v = float(it[1])
            item_id = it[2] if len(it) > 2 else idx
        else:
            raise TypeError(
                "Each item must be a dict with 'weight'/'volume' or a tuple/list of at least two numbers"
            )
        normalized.append((item_id, w, v))

    # Sort by descending combined size (weight + volume) for deterministic packing
    normalized.sort(key=lambda x: (x[1] + x[2], x[1]), reverse=True)

    bins: List[Dict[str, Any]] = []

    for item_id, w, v in normalized:
        # Skip items that cannot fit into an empty bin
        if w > max_weight or v > max_volume:
            continue

        # Find the best existing bin (smallest leftover capacity) that fits
        best_idx = None
        best_slack = None
        for i, b in enumerate(bins):
            if _fits(b, w, v):
                slack = (b["remaining_weight"] - w) + (b["remaining_volume"] - v)
                if best_slack is None or slack < best_slack:
                    best_slack = slack
                    best_idx = i

        if best_idx is not None:
            _add_to_bin(bins[best_idx], item_id, w, v)
        else:
            # Open a new bin
            bin_info: Dict[str, Any] = {
                "items": [item_id],
                "total_weight": w,
                "total_volume": v,
                "remaining_weight": max_weight - w,
                "remaining_volume": max_volume - v,
            }
            bins.append(bin_info)

    # Strip auxiliary fields before returning
    for b in bins:
        b.pop("remaining_weight", None)
        b.pop("remaining_volume", None)

    return bins
