import numpy as np
from functools import lru_cache
from typing import List, Any

def _to_array(x):
    """Convert input to a 1‑D float NumPy array."""
    return np.asarray(x, dtype=float).ravel()

def _valid(arr, w):
    """Check that window size is a positive int and fits the array."""
    return isinstance(w, int) and w > 0 and arr.size >= w

@lru_cache(maxsize=64)
def _exp_weights(w):
    """Cached exponential weighting vector that sums to 1."""
    w = int(w)
    weights = np.exp(np.linspace(-2, 0, w))
    return weights / weights.sum()

def adaptive_filter(x, window_size=20):
    """Simple moving‑average filter."""
    a = _to_array(x)
    w = int(window_size)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    return np.convolve(a, np.ones(w) / w, mode="valid")

def weighted_filter(x, window_size=20):
    """Exponentially weighted moving‑average filter."""
    a = _to_array(x)
    w = int(window_size)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    return np.convolve(a, _exp_weights(w), mode="valid")

def ema_filter(x, window_size=20):
    """Recursive exponential moving‑average (EMA) filter."""
    a = _to_array(x)
    w = int(window_size)
    if not _valid(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(a)
    ema[0] = a[0]
    for i in range(1, a.size):
        ema[i] = alpha * a[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]

def hybrid_filter(x, window_size=20):
    """Average of adaptive and weighted filters (same length)."""
    a = adaptive_filter(x, window_size)
    b = weighted_filter(x, window_size)
    if a.size == 0:
        return b
    if b.size == 0:
        return a
    return (a + b) / 2.0

def _residual_variance(orig, filt):
    """Variance of the residual over the overlapping region."""
    n = min(len(orig), len(filt))
    if n == 0:
        return float("inf")
    return float(np.var(orig[:n] - filt[:n]))

def _auto_select(x, window_size=20):
    """Pick the filter with the lowest residual variance."""
    a = _to_array(x)
    w = int(window_size)
    candidates = {
        "adaptive": adaptive_filter(a, w),
        "weighted": weighted_filter(a, w),
        "ema": ema_filter(a, w),
        "hybrid": hybrid_filter(a, w),
    }
    # Discard empty results
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
        return np.empty(0, dtype=float)
    best_key = min(candidates, key=lambda k: _residual_variance(a, candidates[k]))
    return candidates[best_key]

def process_signal(input_signal, window_size=20, algorithm_type="enhanced"):
    """Dispatch to a filtering algorithm.

    Supported ``algorithm_type`` values (case‑insensitive):
        - "adaptive" / "basic"
        - "weighted"
        - "ema"
        - "enhanced" / "hybrid"
        - "auto"
    Unknown values fall back to the hybrid filter.
    """
    alg = (algorithm_type or "").lower()
    if alg in ("adaptive", "basic"):
        return adaptive_filter(input_signal, window_size)
    if alg == "weighted":
        return weighted_filter(input_signal, window_size)
    if alg == "ema":
        return ema_filter(input_signal, window_size)
    if alg in ("enhanced", "hybrid"):
        return hybrid_filter(input_signal, window_size)
    if alg == "auto":
        return _auto_select(input_signal, window_size)
    # Fallback
    return hybrid_filter(input_signal, window_size)

def construct_packing(items: List[Any], capacity: int) -> List[List[int]]:
    """
    First‑Fit Decreasing bin‑packing.

    Args:
        items: Iterable of non‑negative numeric sizes.
        capacity: Positive integer representing bin capacity.

    Returns:
        A list of bins; each bin is a list of original item indices that
        fit within the given capacity.  Items that exceed the capacity are
        placed in their own bin.
    """
    # Basic validation
    try:
        items_list = list(items)
    except Exception:
        return []

    if not isinstance(capacity, int) or capacity <= 0:
        raise ValueError("capacity must be a positive integer")

    if not items_list:
        return []

    # Pair each item with its original index
    indexed = [(idx, size) for idx, size in enumerate(items_list)]

    # Sort by size descending (First‑Fit Decreasing)
    indexed.sort(key=lambda pair: pair[1], reverse=True)

    bins: List[List[int]] = []
    remaining: List[int] = []  # remaining capacity per bin

    for idx, size in indexed:
        # Skip invalid (negative) sizes
        if not isinstance(size, (int, float)) or size < 0:
            continue

        if size > capacity:
            # Item too large – allocate its own bin
            bins.append([idx])
            remaining.append(0)
            continue

        placed = False
        for b_idx, space in enumerate(remaining):
            if size <= space:
                bins[b_idx].append(idx)
                remaining[b_idx] -= size
                placed = True
                break

        if not placed:
            # Open a new bin
            bins.append([idx])
            remaining.append(capacity - size)

    # Sort indices within each bin for deterministic output
    for bin_contents in bins:
        bin_contents.sort()

    return bins
