    """Uniform moving average."""
    return a.copy() if w == 1 else np.convolve(a, np.ones(w) / w, mode="valid")

def _exp_ma(a, w):
    """Exponentially weighted moving average."""
    return a.copy() if w == 1 else np.convolve(a, _exp_weights(w)[::-1], mode="valid")

def enhanced_blend(x, w=20):
    """Blend simple and exponential weighted averages."""
