    """Variance of the difference over the overlapping region."""
    n = min(len(orig), len(filt))
    return np.inf if n == 0 else float(np.var(orig[:n] - filt[:n]))

def process_signal(sig, w=20, alg="auto"):
    """Dispatch to a filter or auto‑select the best based on residual variance."""
    a = alg.lower()
    if a == "adaptive":
        a_arr, ok = _prepare(sig, w)
        return _simple_ma(a_arr, w) if ok else np.empty(0, dtype=float)
