import numpy as np

def _to_array(x):
    """Convert input to a 1‑D float ndarray."""
    return np.asarray(x, dtype=float).ravel()

def _window_ok(arr, w):
    """Validate that window size is positive and fits the array."""
    return isinstance(w, int) and w > 0 and arr.size >= w

def adaptive_filter(sig, w=20):
    """Simple moving‑average (uniform) filter."""
    a = _to_array(sig)
    if not _window_ok(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    return np.convolve(a, np.ones(w) / w, mode="valid")

def weighted_filter(sig, w=20):
    """Exponentially weighted moving‑average filter."""
    a = _to_array(sig)
    if not _window_ok(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    wts = np.exp(np.linspace(-2, 0, w))
    wts /= wts.sum()
    return np.convolve(a, wts, mode="valid")

def ema_filter(sig, w=20):
    """Classic exponential moving average (EMA)."""
    a = _to_array(sig)
    if not _window_ok(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    alpha = 2.0 / (w + 1)
    ema = np.empty_like(a)
    ema[0] = a[0]
    for i in range(1, a.size):
        ema[i] = alpha * a[i] + (1 - alpha) * ema[i - 1]
    return ema[w - 1 :]

def enhanced_filter_with_trend_preservation(sig, w=20):
    """
    Detrend using a simple moving average, then smooth the residual
    with an exponentially weighted filter.
    """
    a = _to_array(sig)
    if not _window_ok(a, w):
        return np.empty(0, dtype=float)
    if w == 1:
        return a.copy()
    # 1) Estimate trend (uniform moving average)
    trend = adaptive_filter(a, w)
    # Pad the beginning so trend aligns with original length
    pad = np.full(w - 1, trend[0], dtype=a.dtype)
    trend_full = np.concatenate((pad, trend))
    # 2) Detrend and apply weighted smoothing
    detrended = a - trend_full
    return weighted_filter(detrended, w)

def _residual_variance(orig, filt):
    """Mean squared error between original and filtered signal over the overlap."""
    n = min(len(orig), len(filt))
    if n == 0:
        return float("inf")
    return float(np.var(orig[:n] - filt[:n]))

def _select_best_filter(sig, w=20):
    """Choose the filter with the smallest residual variance."""
    a = _to_array(sig)
    candidates = {
        "adaptive": adaptive_filter(a, w),
        "weighted": weighted_filter(a, w),
        "ema": ema_filter(a, w),
        "enhanced": enhanced_filter_with_trend_preservation(a, w),
    }
    # discard empty results
    candidates = {k: v for k, v in candidates.items() if v.size}
    if not candidates:
        return np.empty(0, dtype=float)
    best_key = min(candidates, key=lambda k: _residual_variance(a, candidates[k]))
