import numpy as np

def _ma(x: np.ndarray, w: int, wt: np.ndarray) -> np.ndarray:
    """Convolution helper for moving‑average‑style filters."""
    x = np.asarray(x, float)
    if w <= 0:
        raise ValueError("window must be >0")
    if x.size < w:
        return np.empty(0, float)
    return np.convolve(x, wt, mode="valid")


def simple_ma(x, w):
    """Uniform moving average."""
    return _ma(x, w, np.ones(w) / w)


def weighted_ma(x, w):
    """Exponentially weighted moving average (weights decay towards older samples)."""
    wt = np.exp(np.linspace(-2, 0, w))
    wt /= wt.sum()
    return _ma(x, w, wt)


def ema(x, w):
    """Classic exponential moving average (recursive formulation)."""
    x = np.asarray(x, float)
    if w <= 0:
        raise ValueError("window must be >0")
    if x.size < w:
        return np.empty(0, float)
    alpha = 2.0 / (w + 1)
    out = np.empty_like(x)
    out[0] = x[0]
    for i in range(1, len(x)):
        out[i] = alpha * x[i] + (1 - alpha) * out[i - 1]
    return out[w - 1 :]


def detrended_weighted(x, w):
    """Detrend with a simple MA then apply a weighted MA to the residual."""
    tr = simple_ma(x, w)
    tr_full = np.concatenate((np.full(w - 1, tr[0]), tr))
    return weighted_ma(x - tr_full, w)


def gaussian_ma(x, w):
    """Moving average with Gaussian kernel (smooth, bell‑shaped weighting)."""
    x = np.asarray(x, float)
    if w <= 0:
        raise ValueError("window must be >0")
    if x.size < w:
        return np.empty(0, float)
    sigma = max(w / 6.0, 0.1)
    centre = (w - 1) / 2.0
    i = np.arange(w, dtype=float)
    wt = np.exp(-0.5 * ((i - centre) / sigma) ** 2)
    wt /= wt.sum()
    return _ma(x, w, wt)


def median_ma(x, w):
    """Median filter – robust to outliers/spikes."""
    x = np.asarray(x, float)
    if w <= 0:
        raise ValueError("window must be >0")
    if x.size < w:
        return np.empty(0, float)
    out_len = x.size - w + 1
    out = np.empty(out_len, float)
